import React, { useContext } from 'react';
import bgImage from '../assets/bg.png';
import Dicebox from '../assets/diceBoard.png';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';
import { GameContext } from '../context/GameContext';

const GameScreen = () => {
  const { player, currentPlayerId } = useContext(GameContext);
  const isMyTurn = player && player.socketId === currentPlayerId;

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        position: 'relative',
        backgroundImage: `url(${bgImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        overflow: 'hidden',
      }}
    >
      {/* Floating top window divided into 4 quarters */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '240px',
          display: 'flex',
          zIndex: 100,
          background: 'rgba(164, 164, 164, 0.9)',
          boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
        }}
      >
        {/* 1st quarter: conditional dice background */}
        <div
          style={{
            flex: 1,
            position: 'relative',
            height: '100%',
            borderRight: '1px solid #333',
            background: isMyTurn
              ? `url(${Dicebox}) center/cover no-repeat`
              : 'none',
          }}
        >
          <DiceRoller />
        </div>

        {/* Empty 2nd quarter */}
        <div style={{ flex: 1, borderRight: '1px solid #333', height: '100%' }} />
        {/* Empty 3rd quarter */}
        <div style={{ flex: 1, borderRight: '1px solid #333', height: '100%' }} />
        {/* Empty 4th quarter */}
        <div style={{ flex: 1, height: '100%' }} />
      </div>

      {/* Main content, pushed down by overlay height */}
      <div
        style={{
          marginTop: '240px',
          flex: 1,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
        }}
      >
        {/* Other Players sidebar */}
        <div
          style={{
            minWidth: 220,
            height: '80%',
            marginRight: 48,
            background: 'rgba(206, 206, 206, 0.96)',
            borderRight: '2px solid #d3d3d3',
            borderRadius: 12,
            boxShadow: '0 0 12px 0 #eee',
            padding: '16px 10px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'stretch',
          }}
        >
          <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
            Other Players
          </h4>
          <PlayerStats />
        </div>

        {/* Board in the center */}
        <div style={{ position: 'relative', zIndex: 2 }}>
          <Board />
        </div>
      </div>

      {/* Bottom Dashboard */}
      <div
        style={{
          width: '100%',
          background: '#fff',
          borderTop: '2px solid #bbb',
          boxShadow: '0 -2px 10px #ececec',
          padding: '0 0 18px 0',
          zIndex: 10,
        }}
      >
        <Dashboard />
      </div>
    </div>
  );
};

export default GameScreen;
