import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';

// List of piece images (names should match your /public/pieces folder)
const allPieces = [
  'piece1.png', 'piece2.png', 'piece3.png', 'piece4.png',
  'piece5.png', 'piece6.png', 'piece7.png', 'piece8.png'
];

const Lobby = () => {
  const {
    player,
    setPlayer,
    players = [],
    setPlayers,
    setGameState,
    socket
  } = useContext(GameContext);

  // Local UI state
  const [name, setName] = useState('');
  const [hasJoined, setHasJoined] = useState(false);
  const [selectedPiece, setSelectedPiece] = useState('');

  useEffect(() => {
    socket.on('lobbyUpdate', updatedPlayers => {
      setPlayers(updatedPlayers || []);
    });

    socket.on('gameStart', () => {
      setGameState('playing');
    });

    return () => {
      socket.off('lobbyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState, socket]);

  // When player joins the lobby
  const joinLobby = () => {
    if (!name.trim()) return;
    setPlayer({ name });
    setHasJoined(true);
    socket.emit('joinLobby', { name }); // backend creates player object
  };

  // When player selects a piece
  const choosePiece = piece => {
    setSelectedPiece(piece);
    setPlayer(prev => ({ ...prev, piece }));
    socket.emit('selectPiece', { piece });
  };

  // When player clicks "Ready"
  const handleReady = () => {
    socket.emit('playerReady');
  };

  // Only allow unique pieces
  const usedPieces = players.map(p => p.piece).filter(Boolean);
  const availablePieces = allPieces.filter(p => !usedPieces.includes(p));

  // Calculate readiness (server controls when game starts)
  const thisPlayer = players.find(p => p.name === player?.name);
  const showReadyButton = !!selectedPiece && thisPlayer && !thisPlayer.ready;

  return (
    <div style={{
      display: 'flex', flexDirection: 'column',
      alignItems: 'center', justifyContent: 'center',
      height: '100vh', fontSize: '1.2rem'
    }}>
      <h2>Business Lobby</h2>

      {/* 1. Join Screen */}
      {!hasJoined ? (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            style={{ marginBottom: '1rem', fontSize: '1.2rem' }}
          />
          <button onClick={joinLobby}>Join Lobby</button>
        </>
      ) : /* 2. Piece selection */ !selectedPiece ? (
        <>
          <p>Select your game piece:</p>
          <div style={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: '10px',
            justifyContent: 'center'
          }}>
            {availablePieces.map((piece, idx) => (
              <div key={idx} style={{ textAlign: 'center' }}>
                <img
                  src={`/pieces/${piece}`}
                  alt={piece}
                  style={{ width: 60, height: 60, cursor: 'pointer' }}
                  onClick={() => choosePiece(piece)}
                />
                <div>{piece.replace('.png', '')}</div>
              </div>
            ))}
          </div>
        </>
      ) : (
        /* 3. Wait for Ready */
        <>
          <p>
            Welcome, <strong>{player.name}</strong>! Youâ€™ve selected:{' '}
            <img src={`/pieces/${selectedPiece}`} alt="" style={{ width: 30 }} />
          </p>
          {showReadyButton &&
            <button onClick={handleReady}>Ready</button>
          }
          {!showReadyButton && <p>Waiting for others...</p>}
        </>
      )}

      <hr style={{ width: '60%', margin: '2rem 0' }} />

      <h3>Players in Lobby:</h3>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {players.map((p, idx) => (
          <li key={idx}>
            {p.name}{' '}
            {p.piece && (
              <>
                <img src={`/pieces/${p.piece}`} alt="" style={{ width: 20, verticalAlign: 'middle' }} />
              </>
            )}
            {p.ready && ' âœ…'}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Lobby;
