// src/components/GameController.js
import { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const delay = ms => new Promise(res => setTimeout(res, ms));

const GameController = () => {
  const {
    players,
    setPlayers,
    currentPlayerIndex,
    originalCount,
    isMoving,
    setIsMoving
  } = useContext(GameContext);

  useEffect(() => {
    const movePlayer = async () => {
      if (!isMoving || originalCount <= 0) return;

      const updatedPlayers = [...players];
      const player = updatedPlayers[currentPlayerIndex];

      for (let i = 0; i < originalCount; i++) {
        const currentTile = tiles.find(t => t.id === player.tileId);
        let options = currentTile.next;

        // Remove backtrack option
        options = options.filter(opt => opt.from !== player.prevTile);

        let nextId = null;
        if (options.length === 1) {
          nextId = options[0].to;
        } else if (options.length === 2) {
          if (originalCount === 7) {
            const confirm = window.confirm("Go left path?");
            nextId = confirm ? options[0].to : options[1].to;
          } else {
            const rollType = originalCount > 7 ? 'above' : 'below';
            nextId = options.find(o => o.roll === rollType)?.to || options[0].to;
          }
        } else {
          nextId = options[0].to; // fallback
        }

        player.prevTile = player.tileId;
        player.tileId = nextId;
        setPlayers([...updatedPlayers]);
        await delay(500);
      }

      setIsMoving(false);
    };

    movePlayer();
  }, [isMoving, originalCount, players, currentPlayerIndex, setIsMoving, setPlayers]);

  return null;
};

export default GameController;
