import React, { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';

export default function Dashboard() {
  const { player } = useContext(GameContext);
  
  useEffect(() => {
    console.log('[Dashboard] Player money updated:', {
      playerName: player?.name,
      money: player?.money
    });
  }, [player?.money]);

  if (!player) return null;

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      marginTop: '1.5rem'
    }}>
      <div style={{
        border: '2px solid #444',
        borderRadius: 10,
        padding: '1.5rem 2rem',
        width: '80%',
        maxWidth: 700,
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        boxShadow: '0 2px 6px rgba(0, 0, 0, 0.2)',
        textAlign: 'center'
      }}>
        <h3 style={{ 
          fontSize: '1.8rem', 
          marginBottom: '1rem',
          color: '#fff'
        }}>{player.name}'s Dashboard</h3>
        <div style={{ 
          fontSize: '2.2rem', 
          fontWeight: 'bold',
          color: '#4CAF50',
          marginBottom: '10px'
        }}>
          ${player.money?.toLocaleString() || 0}
        </div>
        {(player.loan > 0) && (
          <div style={{ 
            fontSize: '1.4rem',
            color: '#ff9999',
            marginTop: '5px'
          }}>
            Loan: ${player.loan?.toLocaleString()}
          </div>
        )}
      </div>
    </div>
  );
}
