import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const socket = io('http://localhost:3001');

const allPieces = [
  { name: 'piece1.png', src: piece1 },
  { name: 'piece2.png', src: piece2 },
  { name: 'piece3.png', src: piece3 },
  { name: 'piece4.png', src: piece4 },
  { name: 'piece5.png', src: piece5 },
  { name: 'piece6.png', src: piece6 },
  { name: 'piece7.png', src: piece7 },
  { name: 'piece8.png', src: piece8 },
];

const Lobby = () => {
  const { player, setPlayer, players, setPlayers, setGameState } = useContext(GameContext);
  const [name, setName] = useState('');
  const [hasJoined, setHasJoined] = useState(false);
  const [selectedPiece, setSelectedPiece] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', setPlayers);
    socket.on('readyUpdate', setReadyPlayers);
    socket.on('gameStart', () => setGameState('playing'));

    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = { name, piece: null, ready: false };
    setPlayer(newPlayer);
    setHasJoined(true);
    socket.emit('joinLobby', newPlayer);
  };

  const choosePiece = (p) => {
    const updated = { ...player, piece: p.name };
    setSelectedPiece(p.name);
    setPlayer(updated);
    // emit only the piece name string
    socket.emit('selectPiece', p.name);
  };

  const handleReady = () => {
    socket.emit('playerReady', player.name);
  };

  const usedNames = players.map(p => p.piece).filter(Boolean);
  const available = allPieces.filter(p => !usedNames.includes(p.name));
  const allReady = players.length >= 2 && players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{
      display: 'flex', flexDirection: 'column',
      alignItems: 'center', justifyContent: 'center',
      height: '100vh', fontSize: '1.2rem'
    }}>
      <h2>Monopoly Lobby</h2>

      {!hasJoined ? (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            style={{ marginBottom: '1rem', fontSize: '1.2rem' }}
          />
          <button onClick={joinLobby}>Join Lobby</button>
        </>
      ) : !selectedPiece ? (
        <>
          <p>Select your game piece:</p>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', justifyContent: 'center' }}>
            {available.map((p, i) => (
              <div key={i} onClick={() => choosePiece(p)} style={{ textAlign: 'center', cursor: 'pointer' }}>
                <img src={p.src} alt={p.name} width={60} height={60} />
                <div>{p.name.replace('.png','')}</div>
              </div>
            ))}
          </div>
        </>
      ) : (
        <>
          <p>
            Welcome, <strong>{player.name}</strong>! You picked{' '}
            <strong>{selectedPiece.replace('.png','')}</strong>
          </p>
          <button onClick={handleReady}>Ready</button>
        </>
      )}

      <hr style={{ width: '60%', margin: '2rem 0' }} />

      <h3>Players:</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {players.map((p, i) => (
          <li key={i}>
            {p.name}{p.piece && ` — ${p.piece.replace('.png','')}`}{readyPlayers.includes(p.name) && ' ✅'}
          </li>
        ))}
      </ul>

      {allReady && <p style={{ color: 'green' }}>All players ready! Starting…</p>}
    </div>
  );
};

export default Lobby;
