// src/components/Dashboard.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const Dashboard = () => {
  const {
    players,
    diceRoll,
    isMoving,
    player,
    setCurrentPlayerIndex,
    setDiceRoll
  } = useContext(GameContext);

  const me = player;

  const handleDone = () => {
    // Advance turn, clear any leftover roll
    setDiceRoll(null);
    setCurrentPlayerIndex(prev => (prev + 1) % players.length);
  };

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      marginTop: '1.5rem',
      fontSize: '1.1rem'
    }}>
      <div style={{
        border: '2px solid #444',
        borderRadius: '10px',
        padding: '1.2rem 2rem',
        width: '80%',
        maxWidth: '700px',
        backgroundColor: '#ffffff',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        textAlign: 'center'
      }}>
        <h3>{me.name}'s Dashboard</h3>
        <p><strong>Money:</strong> ${me.money || 0}</p>
        <p><strong>Properties:</strong> {me.properties?.length ? me.properties.join(', ') : 'None'}</p>

        {/* Guidance & Done button logic */}
        {!diceRoll && !isMoving && (
          <p style={{ color: '#888' }}>Roll the dice to start your turn.</p>
        )}
        {!isMoving && diceRoll (
          <button onClick={handleDone} style={{ marginTop: '1rem' }}>
            Done
          </button>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
