import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    player,
    players,
    currentPlayerIndex,
    setOriginalCount,
    isMoving,
    setIsMoving,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  const [dice, setDice] = useState(null);

  // only show on the active player's turn
  const isMyTurn = players[currentPlayerIndex]?.name === player?.name;
  if (!isMyTurn) return null;

  const roll = () => {
    if (isMoving) return;
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDice([d1, d2]);
    setOriginalCount(d1 + d2);
    setIsMoving(true);
  };

  const done = () => {
    setDice(null);
    setCurrentPlayerIndex(idx => (idx + 1) % players.length);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: 'white',
      padding: '1rem',
      border: '2px solid #333',
      borderRadius: 8,
      textAlign: 'center',
      zIndex: 1000
    }}>
      <h3>Your Turn</h3>

      {!dice && (
        <button onClick={roll}>Roll Dice</button>
      )}

      {dice && (
        <div style={{ display: 'flex', justifyContent: 'center', gap: 10, marginTop: 10 }}>
          <img src={require(`../assets/dice/dice${dice[0]}.png`)} alt="" width={50} />
          <img src={require(`../assets/dice/dice${dice[1]}.png`)} alt="" width={50} />
        </div>
      )}

      {!isMoving && dice && (
        <button onClick={done} style={{ marginTop: 10 }}>
          Done
        </button>
      )}
    </div>
  );
};

export default DiceRoller;
