import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    player: localPlayer,
    players,
    currentPlayerIndex,
    isMoving,
    setOriginalCount,
    setIsMoving
  } = useContext(GameContext);

  const [dice, setDice] = useState(null);
  const currentPlayer = players[currentPlayerIndex];

  // Only render for the local player when it's their turn
  if (
    !localPlayer ||
    !currentPlayer ||
    currentPlayer.name !== localPlayer.name
  ) {
    return null;
  }

  const handleRoll = () => {
    if (isMoving) return;
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDice([d1, d2]);
    setOriginalCount(d1 + d2);
    setIsMoving(true);
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        background: '#fff',
        padding: '1rem',
        border: '2px solid #333',
        borderRadius: '12px',
        boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
        textAlign: 'center',
        zIndex: 1000
      }}
    >
      <h3>Your Turn</h3>
      {!dice && <button onClick={handleRoll}>Roll Dice</button>}
      {dice && (
        <div style={{ margin: '1rem', display: 'flex', justifyContent: 'center' }}>
          <img
            src={`/dice/dice${dice[0]}.png`}
            alt={`Die ${dice[0]}`}
            width={50}
          />
          <img
            src={`/dice/dice${dice[1]}.png`}
            alt={`Die ${dice[1]}`}
            width={50}
            style={{ marginLeft: 10 }}
          />
        </div>
      )}
    </div>
  );
};

export default DiceRoller;
