// src/components/Board.js
import React, { useContext, useEffect, useRef, useState } from 'react';
import { GameContext } from '../context/GameContext';
import boardImg from '../assets/board.png';

const tilePositions = {
  1: { x: 100, y: 100 },
  // You can fill this out with actual tile center positions later
};

const Board = () => {
  const canvasRef = useRef(null);
  const [mouseCoords, setMouseCoords] = useState({ x: 0, y: 0 });
  const { players } = useContext(GameContext);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const board = new Image();
    board.src = boardImg;

    board.onload = () => {
      canvas.width = board.width;
      canvas.height = board.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(board, 0, 0);

      players.forEach((player, idx) => {
        if (player.piece) {
          const pieceImg = new Image();
          pieceImg.src = require(`../assets/pieces/${player.piece}`);

          pieceImg.onload = () => {
            const base = tilePositions[player.tileId || 1] || { x: 100, y: 100 };
            // Offset each player's piece slightly so they don't overlap
            const offsetX = idx * 35;
            ctx.drawImage(pieceImg, base.x + offsetX, base.y, 30, 30);
          };
        }
      });
    };
  }, [players]);

  const handleMouseMove = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = Math.floor(e.clientX - rect.left);
    const y = Math.floor(e.clientY - rect.top);
    setMouseCoords({ x, y });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <canvas
        ref={canvasRef}
        onMouseMove={handleMouseMove}
        style={{ border: '2px solid black' }}
      />
      <div style={{ marginTop: '10px' }}>
        Mouse Coordinates: X: {mouseCoords.x}, Y: {mouseCoords.y}
      </div>
    </div>
  );
};

export default Board;
