import { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const delay = ms => new Promise(res => setTimeout(res, ms));

const GameController = () => {
  const {
    players,
    setPlayers,
    currentPlayerIndex,
    originalCount,
    setOriginalCount,
    isMoving,
    setIsMoving
  } = useContext(GameContext);

  useEffect(() => {
    const movePlayer = async () => {
      if (!isMoving || originalCount <= 0) return;

      let steps = originalCount;
      const updated = [...players];
      const p = updated[currentPlayerIndex];

      while (steps > 0) {
        const tile = tiles.find(t => t.id === p.tileId);
        // filter out backtrack option
        const opts = tile.next.filter(o => o.from === p.prevTile || o.from == null);
        const nextId = opts[0]?.to ?? p.tileId;

        p.prevTile = p.tileId;
        p.tileId = nextId;
        setPlayers([...updated]);

        steps -= 1;
        setOriginalCount(steps);
        await delay(300);
      }

      setIsMoving(false);
    };

    movePlayer();
  }, [
    isMoving,
    originalCount,
    currentPlayerIndex,
    players,
    setPlayers,
    setOriginalCount,
    setIsMoving
  ]);

  return null;
};

export default GameController;
