import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const PlayerStats = () => {
  const { players = [], player } = useContext(GameContext);

  if (!Array.isArray(players) || players.length === 0 || !player || !player.socketId) return null;

  // Show all other players except the local player
  const others = players.filter(p => p && p.socketId !== player.socketId);

  if (others.length === 0) {
    return null;
  }

  // Calculate positions based on number of players
  const getPositions = (numPlayers) => {
    switch(numPlayers) {
      case 1: // Just one other player
        return [{ position: 'left' }];
      case 2: // Two other players
        return [{ position: 'left', top: '25%' }, { position: 'left', top: '75%' }];
      case 3: // Three other players
        return [
          { position: 'left', top: '25%' },
          { position: 'left', top: '75%' },
          { position: 'top', left: '50%' }
        ];
      case 4: // Four other players
        return [
          { position: 'left', top: '25%' },
          { position: 'left', top: '75%' },
          { position: 'top', left: '33%' },
          { position: 'top', left: '66%' }
        ];
      case 5: // Five other players
        return [
          { position: 'left', top: '25%' },
          { position: 'left', top: '75%' },
          { position: 'right', top: '25%' },
          { position: 'right', top: '75%' },
          { position: 'top', left: '50%' }
        ];
      case 6: // Six other players
        return [
          { position: 'left', top: '25%' },
          { position: 'left', top: '75%' },
          { position: 'right', top: '25%' },
          { position: 'right', top: '75%' },
          { position: 'top', left: '33%' },
          { position: 'top', left: '66%' }
        ];
      case 7: // Seven other players
        return [
          { position: 'left', top: '25%' },
          { position: 'left', top: '75%' },
          { position: 'right', top: '25%' },
          { position: 'right', top: '75%' },
          { position: 'top', left: '25%' },
          { position: 'top', left: '50%' },
          { position: 'top', left: '75%' }
        ];
      default:
        return [];
    }
  };

  const positions = getPositions(others.length);

  return (
    <>
      {others.map((p, idx) => {
        const pos = positions[idx];
        if (!pos) return null;

        const style = {
          position: 'absolute',
          border: '1px solid black',
          padding: '10px',
          background: 'rgba(80, 80, 80, 0.61)',
          fontSize: '0.9rem',
          color: 'white',
          width: '180px',
          borderRadius: '8px',
          boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
          zIndex: 1,
          transform: 'translate(0, -50%)'  // Default transform
        };

        // Apply position based on placement
        if (pos.position === 'left') {
          style.right = '100%';  // Align to left edge of board
          style.marginRight = '20px';  // Add 20px spacing
          style.top = pos.top || '50%';
        } else if (pos.position === 'right') {
          style.left = '100%';   // Align to right edge of board
          style.marginLeft = '20px';   // Add 20px spacing
          style.top = pos.top || '50%';
        } else if (pos.position === 'top') {
          style.bottom = '100%'; // Align to top edge of board
          style.marginBottom = '20px'; // Add 20px spacing
          style.left = pos.left || '50%';
          style.transform = 'translate(-50%, 0)'; // Center horizontally
          style.top = 'auto'; // Reset top
        }

        return (
          <div key={p.socketId || idx} style={style}>
            <strong>{p.name}</strong>'s Stats<br />
            Money: ${typeof p.money === 'number' ? p.money : 0}<br />
            Properties:{' '}
            {Array.isArray(p.properties) && p.properties.length > 0
              ? p.properties.join(', ')
              : 'None'}
          </div>
        );
      })}
    </>
  );
};

export default PlayerStats;
