import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';

const GameScreen = () => {
  const { gameState } = useContext(GameContext);

  // Only render if the game is active/playing
  if (gameState !== 'playing') return null;

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        background: '#f3f3f7',
        overflow: 'hidden',
      }}
    >
      {/* Main row: Board in center, PlayerStats on right */}
      <div
        style={{
          flex: 1,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
        }}
      >
        {/* Board in the center, DiceRoller overlays on top */}
        <div style={{ position: 'relative', zIndex: 2 }}>
          <Board />
          <DiceRoller />
        </div>

        {/* PlayerStats on the right */}
        <div
          style={{
            minWidth: 220,
            height: '80%',
            marginLeft: 48,
            background: 'rgba(255,255,255,0.96)',
            borderLeft: '2px solid #d3d3d3',
            borderRadius: 12,
            boxShadow: '0 0 12px 0 #eee',
            padding: '16px 10px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'stretch',
          }}
        >
          <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
            Other Players
          </h4>
          <PlayerStats />
        </div>
      </div>

      {/* Player's own dashboard at the bottom */}
      <div
        style={{
          width: '100%',
          background: '#fff',
          borderTop: '2px solid #bbb',
          boxShadow: '0 -2px 10px #ececec',
          padding: '0 0 18px 0',
          zIndex: 10,
        }}
      >
        <Dashboard />
      </div>
    </div>
  );
};

export default GameScreen;
