import React, { useState, useContext } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const TradePanel = () => {
  const { socket, player, players } = useContext(GameContext);

  // Offer section state
  const [offerMoney, setOfferMoney] = useState(0);
  const [selectedOfferProperties, setSelectedOfferProperties] = useState([]);
  const [isOfferExpanded, setIsOfferExpanded] = useState(false);
  const [isOfferPropertiesExpanded, setIsOfferPropertiesExpanded] = useState(false);
  const [offerReady, setOfferReady] = useState(false);

  // Ask section state
  const [askMoney, setAskMoney] = useState(0);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [selectedAskProperties, setSelectedAskProperties] = useState([]);
  const [isAskExpanded, setIsAskExpanded] = useState(false);
  const [isAskPropertiesExpanded, setIsAskPropertiesExpanded] = useState(false);
  const [askReady, setAskReady] = useState(false);

  // Incoming offers state
  const [incomingOffers, setIncomingOffers] = useState([]);

  // Effect to listen for trade requests
  React.useEffect(() => {
    if (!socket) return;

    socket.on('tradeRequest', (offer) => {
      if (offer.to === player?.socketId) {
        setIncomingOffers(prev => [...prev, offer]);
      }
    });

    socket.on('tradeAccepted', ({ offerId }) => {
      setIncomingOffers(prev => prev.filter(offer => offer.id !== offerId));
    });

    socket.on('tradeRejected', ({ offerId }) => {
      setIncomingOffers(prev => prev.filter(offer => offer.id !== offerId));
    });

    return () => {
      socket.off('tradeRequest');
      socket.off('tradeAccepted');
      socket.off('tradeRejected');
    };
  }, [socket, player]);

  // Update ready states
  React.useEffect(() => {
    setOfferReady(offerMoney >= 500 || selectedOfferProperties.length > 0);
  }, [offerMoney, selectedOfferProperties]);

  React.useEffect(() => {
    setAskReady(askMoney >= 500 || selectedAskProperties.length > 0);
  }, [askMoney, selectedAskProperties]);

  const handleMoneyChange = (section, amount) => {
    if (section === 'offer') {
      const newAmount = Math.max(0, Math.min(offerMoney + amount, player.money));
      setOfferMoney(newAmount);
    } else {
      const newAmount = Math.max(0, askMoney + amount);
      setAskMoney(newAmount);
    }
  };

  const handlePropertyToggle = (section, propertyId) => {
    if (section === 'offer') {
      setSelectedOfferProperties(prev => 
        prev.includes(propertyId) 
          ? prev.filter(id => id !== propertyId)
          : [...prev, propertyId]
      );
    } else {
      setSelectedAskProperties(prev => 
        prev.includes(propertyId) 
          ? prev.filter(id => id !== propertyId)
          : [...prev, propertyId]
      );
    }
  };

  const handlePlayerSelect = (playerId) => {
    setSelectedPlayer(playerId);
    setSelectedAskProperties([]); // Reset selected properties when player changes
    setIsAskPropertiesExpanded(false);
  };

  const handleTradeRequest = () => {
    if (!offerReady || !askReady || !selectedPlayer) return;

    const tradeRequest = {
      from: player.socketId,
      to: selectedPlayer,
      offer: {
        money: offerMoney,
        properties: selectedOfferProperties
      },
      ask: {
        money: askMoney,
        properties: selectedAskProperties
      },
      id: Date.now() // Simple unique ID
    };

    socket.emit('tradeRequest', tradeRequest);

    // Reset form
    setOfferMoney(0);
    setSelectedOfferProperties([]);
    setAskMoney(0);
    setSelectedAskProperties([]);
    setSelectedPlayer(null);
    setIsOfferExpanded(false);
    setIsAskExpanded(false);
  };

  const handleTradeResponse = (offerId, accepted) => {
    socket.emit('tradeResponse', { offerId, accepted });
  };

  return (
    <div style={{
      backgroundColor: 'rgba(0, 0, 0, 0.8)',
      borderRadius: '10px',
      padding: '15px',
      color: 'white',
      marginBottom: '20px'
    }}>
      <h3 style={{ margin: '0 0 15px 0', textAlign: 'center' }}>Trading</h3>

      {/* Offer Section */}
      <div style={{ marginBottom: '15px' }}>
        <button 
          onClick={() => setIsOfferExpanded(!isOfferExpanded)}
          style={{
            width: '100%',
            padding: '10px',
            backgroundColor: '#4CAF50',
            border: 'none',
            borderRadius: '5px',
            color: 'white',
            cursor: 'pointer',
            marginBottom: '10px'
          }}
        >
          Offer {isOfferExpanded ? '▼' : '▶'}
        </button>

        {isOfferExpanded && (
          <div style={{ padding: '10px' }}>
            {/* Money Input */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              marginBottom: '10px',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              padding: '10px',
              borderRadius: '5px'
            }}>
              <button 
                onClick={() => handleMoneyChange('offer', -500)}
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#f44336',
                  border: 'none',
                  borderRadius: '3px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                -
              </button>
              <span style={{ margin: '0 15px', flex: 1, textAlign: 'center' }}>
                ${offerMoney}
              </span>
              <button 
                onClick={() => handleMoneyChange('offer', 500)}
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#4CAF50',
                  border: 'none',
                  borderRadius: '3px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                +
              </button>
            </div>

            {/* Properties Selection */}
            <button 
              onClick={() => setIsOfferPropertiesExpanded(!isOfferPropertiesExpanded)}
              style={{
                width: '100%',
                padding: '8px',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                borderRadius: '5px',
                color: 'white',
                cursor: 'pointer',
                marginBottom: '10px'
              }}
            >
              Properties {isOfferPropertiesExpanded ? '▼' : '▶'}
            </button>

            {isOfferPropertiesExpanded && (
              <div style={{ 
                marginTop: '10px',
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
                borderRadius: '5px',
                padding: '10px'
              }}>
                {player.properties.map(propId => {
                  const property = tiles.find(t => t.id === propId);
                  return (
                    <div key={propId} style={{ 
                      display: 'flex', 
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      padding: '5px 0'
                    }}>
                      <span>{property?.name}</span>
                      <input
                        type="checkbox"
                        checked={selectedOfferProperties.includes(propId)}
                        onChange={() => handlePropertyToggle('offer', propId)}
                      />
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Ask Section */}
      <div style={{ marginBottom: '15px' }}>
        <button 
          onClick={() => setIsAskExpanded(!isAskExpanded)}
          style={{
            width: '100%',
            padding: '10px',
            backgroundColor: '#2196F3',
            border: 'none',
            borderRadius: '5px',
            color: 'white',
            cursor: 'pointer',
            marginBottom: '10px'
          }}
        >
          Ask {isAskExpanded ? '▼' : '▶'}
        </button>

        {isAskExpanded && (
          <div style={{ padding: '10px' }}>
            {/* Money Input */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              marginBottom: '10px',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              padding: '10px',
              borderRadius: '5px'
            }}>
              <button 
                onClick={() => handleMoneyChange('ask', -500)}
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#f44336',
                  border: 'none',
                  borderRadius: '3px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                -
              </button>
              <span style={{ margin: '0 15px', flex: 1, textAlign: 'center' }}>
                ${askMoney}
              </span>
              <button 
                onClick={() => handleMoneyChange('ask', 500)}
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#4CAF50',
                  border: 'none',
                  borderRadius: '3px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                +
              </button>
            </div>

            {/* Player Selection */}
            <select 
              value={selectedPlayer || ''}
              onChange={(e) => handlePlayerSelect(e.target.value)}
              style={{
                width: '100%',
                padding: '8px',
                marginBottom: '10px',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                borderRadius: '5px',
                color: 'white'
              }}
            >
              <option value="">Select Player</option>
              {players
                .filter(p => p.socketId !== player.socketId)
                .map(p => (
                  <option key={p.socketId} value={p.socketId}>
                    {p.name}
                  </option>
                ))
              }
            </select>

            {/* Properties Selection */}
            {selectedPlayer && (
              <>
                <button 
                  onClick={() => setIsAskPropertiesExpanded(!isAskPropertiesExpanded)}
                  style={{
                    width: '100%',
                    padding: '8px',
                    backgroundColor: 'rgba(255, 255, 255, 0.1)',
                    border: '1px solid rgba(255, 255, 255, 0.2)',
                    borderRadius: '5px',
                    color: 'white',
                    cursor: 'pointer',
                    marginBottom: '10px'
                  }}
                >
                  Properties {isAskPropertiesExpanded ? '▼' : '▶'}
                </button>

                {isAskPropertiesExpanded && (
                  <div style={{ 
                    marginTop: '10px',
                    backgroundColor: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: '5px',
                    padding: '10px'
                  }}>
                    {players
                      .find(p => p.socketId === selectedPlayer)
                      ?.properties.map(propId => {
                        const property = tiles.find(t => t.id === propId);
                        return (
                          <div key={propId} style={{ 
                            display: 'flex', 
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            padding: '5px 0'
                          }}>
                            <span>{property?.name}</span>
                            <input
                              type="checkbox"
                              checked={selectedAskProperties.includes(propId)}
                              onChange={() => handlePropertyToggle('ask', propId)}
                            />
                          </div>
                        );
                      })}
                  </div>
                )}
              </>
            )}
          </div>
        )}
      </div>

      {/* Trade Request Button */}
      <button
        onClick={handleTradeRequest}
        disabled={!offerReady || !askReady || !selectedPlayer}
        style={{
          width: '100%',
          padding: '10px',
          backgroundColor: offerReady && askReady && selectedPlayer ? '#4CAF50' : '#666',
          border: 'none',
          borderRadius: '5px',
          color: 'white',
          cursor: offerReady && askReady && selectedPlayer ? 'pointer' : 'not-allowed',
          marginBottom: '15px'
        }}
      >
        Request Trade
      </button>

      {/* Incoming Offers Section */}
      {incomingOffers.length > 0 && (
        <div>
          <h4 style={{ margin: '15px 0', color: '#4CAF50' }}>Incoming Offers:</h4>
          {incomingOffers.map(offer => {
            const fromPlayer = players.find(p => p.socketId === offer.from);
            return (
              <div key={offer.id} style={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                padding: '10px',
                marginBottom: '10px',
                borderRadius: '5px'
              }}>
                <p style={{ margin: '0 0 10px 0' }}>From: {fromPlayer?.name}</p>
                <div style={{ marginBottom: '10px' }}>
                  <strong>Offers:</strong>
                  <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
                    {offer.offer.money > 0 && (
                      <li>${offer.offer.money}</li>
                    )}
                    {offer.offer.properties.map(propId => {
                      const property = tiles.find(t => t.id === propId);
                      return <li key={propId}>{property?.name}</li>;
                    })}
                  </ul>
                </div>
                <div style={{ marginBottom: '10px' }}>
                  <strong>Asks:</strong>
                  <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
                    {offer.ask.money > 0 && (
                      <li>${offer.ask.money}</li>
                    )}
                    {offer.ask.properties.map(propId => {
                      const property = tiles.find(t => t.id === propId);
                      return <li key={propId}>{property?.name}</li>;
                    })}
                  </ul>
                </div>
                <div style={{ 
                  display: 'flex', 
                  gap: '10px', 
                  marginTop: '10px'
                }}>
                  <button 
                    onClick={() => handleTradeResponse(offer.id, true)}
                    style={{
                      flex: 1,
                      padding: '8px',
                      backgroundColor: '#4CAF50',
                      border: 'none',
                      borderRadius: '3px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    Accept
                  </button>
                  <button 
                    onClick={() => handleTradeResponse(offer.id, false)}
                    style={{
                      flex: 1,
                      padding: '8px',
                      backgroundColor: '#f44336',
                      border: 'none',
                      borderRadius: '3px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    Reject
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default TradePanel;
