// Final verified DiceRoller.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import dice1 from '../assets/dice/dice1.png';
import dice2 from '../assets/dice/dice2.png';
import dice3 from '../assets/dice/dice3.png';
import dice4 from '../assets/dice/dice4.png';
import dice5 from '../assets/dice/dice5.png';
import dice6 from '../assets/dice/dice6.png';

const diceImages = [null, dice1, dice2, dice3, dice4, dice5, dice6];

const DiceRoller = () => {
  const {
    currentPlayerIndex,
    players,
    player,
    originalCount,
    setOriginalCount,
    isMoving,
    setIsMoving,
    diceRoll,
    setDiceRoll,
  } = useContext(GameContext);

  const isCurrentPlayer = players[currentPlayerIndex]?.name === player.name;

  const rollDice = () => {
    if (!isCurrentPlayer || isMoving) return;

    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    const total = d1 + d2;

    setDiceRoll([d1, d2]);
    setOriginalCount(total);
    setIsMoving(true);
  };

  return isCurrentPlayer && !isMoving ? (
    <div style={{
      position: 'absolute',
      top: '30%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: '#fff',
      padding: '20px',
      border: '2px solid black',
      textAlign: 'center',
      zIndex: 20
    }}>
      <h3>Roll the Dice</h3>
      {diceRoll && diceRoll.length === 2 && (
        <div>
          <img src={diceImages[diceRoll[0]]} alt="dice1" width={50} />
          <img src={diceImages[diceRoll[1]]} alt="dice2" width={50} />
        </div>
      )}
      <button onClick={rollDice}>Roll Dice</button>
    </div>
  ) : null;
};

export default DiceRoller;
