// src/components/DiceRoller.js
import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    setOriginalCount,
    setIsMoving,
    isMoving,
    player,
    players,
    currentPlayerIndex,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  const [diceRoll, setDiceRoll] = useState(null);

  const rollDice = () => {
    if (isMoving || diceRoll) return;

    const die1 = Math.ceil(Math.random() * 6);
    const die2 = Math.ceil(Math.random() * 6);
    const total = die1 + die2;

    setDiceRoll([die1, die2]);
    setOriginalCount(total);
    setIsMoving(true);
  };

  const handleDone = () => {
    setDiceRoll(null);
    setCurrentPlayerIndex((prev) => (prev + 1) % players.length);
  };

  if (!player || players[currentPlayerIndex]?.name !== player.name) return null;

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      <h3>Your Turn</h3>
      {!diceRoll && <button onClick={rollDice}>Roll Dice</button>}

      {diceRoll && (
        <div style={{ marginTop: '1rem' }}>
          <img
            src={`../assests/dice/dice${diceRoll[0]}.png`}
            alt="die1"
            style={{ width: '50px', marginRight: '10px' }}
          />
          <img
            src={`/dice/dice${diceRoll[1]}.png`}
            alt="die2"
            style={{ width: '50px' }}
          />
        </div>
      )}

      {!isMoving && diceRoll && (
        <button onClick={handleDone} style={{ marginTop: '1rem' }}>
          Done
        </button>
      )}
    </div>
  );
};

export default DiceRoller;
