import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const PlayerStats = () => {
  const { players = [], player } = useContext(GameContext);

  // LOGGING
  console.log("[PlayerStats.js] players:", players);
  console.log("[PlayerStats.js] local player:", player);

  if (!Array.isArray(players) || players.length === 0 || !player || !player.socketId) return null;

  // Show all other players except the local player
  const others = players.filter(p => p && p.socketId !== player.socketId);

  if (others.length === 0) {
    console.log("[PlayerStats.js] No 'other' players found (local player socketId:", player.socketId, ")");
  }

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',  // <------ this aligns to the left!
      gap: '12px',
      padding: '10px'
    }}>
      {others.map((p, idx) => (
        <div key={p.socketId || idx} style={{
          border: '1px solid black',
          padding: '10px',
          backgroundColor: '#f5f5f5',
          fontSize: '0.9rem'
        }}>
          <strong>{p.name}</strong>'s Stats<br />
          Money: ${typeof p.money === 'number' ? p.money : 0}<br />
          Properties:{' '}
          {Array.isArray(p.properties) && p.properties.length > 0
            ? p.properties.join(', ')
            : 'None'}
        </div>
      ))}
    </div>
  );
};

export default PlayerStats;
