import React, { useState, useContext, useEffect } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyDisplayLeft = ({ player, position }) => {
  const [expandedIndex, setExpandedIndex] = useState(null);

  // Get only this player's properties
  const ownedProperties = tiles
    .filter(tile => tile.type === 'property')
    .filter(tile => player?.properties?.includes(tile.id));

  const handlePropertyClick = (index) => {
    if (index === ownedProperties.length - 1) return;
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  // Reduced dimensions (70% of original) - matching TopPropertyDisplay
  const cardWidth = 196; // 70% of 280 (swapped)
  const cardHeight = 147; // 70% of 210 (swapped)
  const cardOverlap = 39; // 70% of 56
  const expandOffset = 108; // 70% of 154

  // Calculate the total height of the stack when expanded
  const stackHeight = ownedProperties.length * cardOverlap + (expandedIndex !== null ? expandOffset : 0);

  // Position relative to board center (300px = half of board height)
  const centerOffset = position === 'top' ? -150 : 150; // ±150px from center

  return (
    <div style={{
      position: 'absolute',
      right: `calc(100% - 90px)`, 
      top: '50%',
      transform: `translate(0, calc(-50% + ${centerOffset}px))`,
      width: cardWidth + 'px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'visible',
      height: stackHeight + 'px',
      zIndex: 1 // Below the board image which has z-index 2
    }}>
      {ownedProperties.map((property, index) => {
        const isLastProperty = index === ownedProperties.length - 1;
        const isExpanded = isLastProperty || expandedIndex === index;

        // Calculate base position (never changes)
        const basePosition = index * cardOverlap;

        // Calculate additional offset based on expanded state
        let expandedOffset = 0;

        if (expandedIndex !== null) {
          if (index > expandedIndex) {
            expandedOffset = expandOffset;
          }
        }

        const finalPosition = basePosition + expandedOffset;

        return (
          <div
            key={property.id}
            onClick={() => !isLastProperty && handlePropertyClick(index)}
            style={{
              width: cardWidth + 'px',
              height: cardHeight + 'px',
              border: '2px solid #666',
              borderRadius: '8px',
              position: 'absolute',
              top: finalPosition,
              transition: 'all 0.3s ease',
              zIndex: isExpanded ? ownedProperties.length : index,
              display: 'flex',
              flexDirection: 'column',
              padding: '10px',
              boxSizing: 'border-box',
              backgroundColor: 'rgb(173, 216, 230)',
              cursor: isLastProperty ? 'default' : 'pointer'
            }}
          >
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              color: '#333',
              fontSize: '1.2rem',
              fontWeight: 'bold',
              height: '100%'
            }}>
              <div
                style={{
                  fontSize: '1.4rem',
                  marginBottom: '10px',
                  textAlign: 'left',
                  whiteSpace: 'nowrap',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  maxWidth: '176px', // Adjusted for wider card
                  width: '100%'
                }}>
                {property.name}
              </div>
              <div style={{
                fontSize: '1rem',
                opacity: 1,
                textAlign: 'center'
              }}>
                <div>Cost: ${property.cost}</div>
                <div>Rent: ${property.rent}</div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

const PropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const { player, setPlayer, currentPlayerId, socket, players = [] } = useContext(GameContext);
  const [keyInput, setKeyInput] = useState('');

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        const commands = keyInput.trim().toLowerCase();

        // Process 'own#' commands
        const ownMatches = commands.matchAll(/own(\d+)/g);
        const matchesArray = Array.from(ownMatches);
        for (const match of matchesArray) {
          const propertyId = parseInt(match[1], 10);
          const property = tiles.find(tile => tile.id === propertyId);
          if (property) {
            setPlayer(prev => ({
              ...prev,
              properties: [...(prev.properties || []), propertyId]
            }));
          }
        }

        // Process dice roll commands
        if (commands.includes('testroll') || commands.includes('diceroll')) {
          const dice1 = Math.floor(Math.random() * 6) + 1;
          const dice2 = Math.floor(Math.random() * 6) + 1;
        }

        setKeyInput('');
      } else if (e.key.match(/^[0-9a-z]$/i)) {
        setKeyInput(prev => prev + e.key);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [keyInput, currentPlayerId, setPlayer]);

  // Get only current player's properties
  const ownedProperties = tiles
    .filter(tile => tile.type === 'property')
    .filter(tile => player?.properties?.includes(tile.id));

  const handlePropertyClick = (index) => {
    if (index === ownedProperties.length - 1) return;
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  // Get left-side players (based on PlayerStats logic)
  const leftPlayers = players
    .filter(p => p && p.socketId !== player?.socketId)
    .slice(0, 2);  // Take first two players for left side

  return (
    <>
      {/* Bottom property stack for current player */}
      <div style={{
        position: 'absolute',
        top: '100%',
        left: '50%',
        transform: 'translateX(calc(-50% + 50px))',
        height: '280px',
        display: 'flex',
        alignItems: 'flex-start',
        overflow: 'visible',
        marginTop: '-3px',
        width: '800px',
        justifyContent: 'center'
      }}>
        {ownedProperties.map((property, index) => {
          const isLastProperty = index === ownedProperties.length - 1;
          const isExpanded = isLastProperty || expandedIndex === index;
          const basePosition = index * 56;
          let expandedOffset = 0;

          if (expandedIndex !== null && index > expandedIndex) {
            expandedOffset = 154;
          }

          const finalPosition = basePosition + expandedOffset;

          return (
            <div
              key={property.id}
              onClick={() => !isLastProperty && handlePropertyClick(index)}
              style={{
                width: '210px',
                height: '280px',
                border: '2px solid #666',
                borderRadius: '12px',
                position: 'absolute',
                left: finalPosition,
                transition: 'all 0.3s ease',
                zIndex: isExpanded ? ownedProperties.length : index,
                display: 'flex',
                flexDirection: 'column',
                padding: '14px',
                boxSizing: 'border-box',
                backgroundColor: 'rgb(173, 216, 230)',
                cursor: isLastProperty ? 'default' : 'pointer'
              }}
            >
              <div style={{
                transform: 'rotate(-90deg)',
                transformOrigin: 'left center',
                position: 'absolute',
                width: '280px',
                height: '210px',
                top: '140px',
                left: '105px',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'flex-start',
                justifyContent: 'flex-start',
                color: '#333',
                fontSize: '1.68rem',
                fontWeight: 'bold',
                padding: '14px'
              }}>
                <div
                  style={{
                    fontSize: '1.96rem',
                    marginBottom: '14px',
                    textAlign: 'left',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    maxWidth: '252px',
                    width: '100%'
                  }}>
                  {property.name}
                </div>
                <div style={{
                  fontSize: '1.4rem',
                  opacity: 1,
                  textAlign: 'center'
                }}>
                  <div>Cost: ${property.cost}</div>
                  <div>Rent: ${property.rent}</div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Left side property stacks */}
      {leftPlayers.map((leftPlayer, idx) => (
        <PropertyDisplayLeft 
          key={leftPlayer.socketId} 
          player={leftPlayer}
          position={idx === 0 ? 'top' : 'bottom'}
        />
      ))}
    </>
  );
};

export default PropertyDisplay;
