// src/components/Dashboard.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const Dashboard = () => {
  const {
    players,
    currentPlayerIndex,
    isMoving,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  const me = players[currentPlayerIndex];
  if (!me) return null;

  const handleNext = () => {
    setCurrentPlayerIndex(idx => (idx + 1) % players.length);
  };

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      marginTop: '1.5rem',
      fontSize: '1.1rem'
    }}>
      <div style={{
        border: '2px solid #444',
        borderRadius: 10,
        padding: '1rem 2rem',
        backgroundColor: '#fff',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        textAlign: 'center',
        minWidth: 300
      }}>
        <h3>{me.name}'s Dashboard</h3>
        <p><strong>üí∞ Money:</strong> ${me.money}</p>
        <p><strong>üè† Properties:</strong> {me.properties.length ? me.properties.join(', ') : 'None'}</p>
        <div style={{ marginTop: '1rem' }}>
          {!isMoving && (
            <button onClick={handleNext}>Next Player</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
