import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const PlayerStats = () => {
  const { players, player } = useContext(GameContext);
  const others = players.filter(p => p.name !== player?.name);

  return (
    <div style={{
      position: 'absolute',
      top: 10,
      right: 10,
      background: '#f5f5f5',
      padding: 10,
      border: '1px solid #ccc',
      borderRadius: 8,
      maxHeight: 'calc(100% - 20px)',
      overflowY: 'auto'
    }}>
      <h4>Other Players</h4>
      {others.map((p, i) => (
        <div key={i} style={{ marginBottom: 8 }}>
          <strong>{p.name}</strong><br/>
          üí∞ ${p.money}<br/>
          üß© {p.piece || '‚Äî'}<br/>
          üè† {p.properties.length ? p.properties.join(', ') : 'None'}
        </div>
      ))}
    </div>
  );
};

export default PlayerStats;
