import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    setOriginalCount,
    setIsMoving,
    isMoving,
    player,
    players,
    currentPlayerIndex,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  const [diceRoll, setDiceRoll] = useState(null);

  // Only show on the current player's turn
  if (!player || players[currentPlayerIndex]?.name !== player.name) {
    return null;
  }

  const rollDice = () => {
    if (isMoving || diceRoll) return;
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDiceRoll([d1, d2]);
    setOriginalCount(d1 + d2);
    setIsMoving(true);
  };

  const handleDone = () => {
    setDiceRoll(null);
    setCurrentPlayerIndex(idx => (idx + 1) % players.length);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      <h3>Your Turn</h3>
      {!diceRoll && <button onClick={rollDice}>Roll Dice</button>}
      {diceRoll && (
        <div style={{ display: 'flex', justifyContent: 'center', margin: '1rem 0' }}>
          {diceRoll.map((d, i) => (
            <img
              key={i}
              src={`/dice/dice${d}.png`}
              alt={`Die ${d}`}
              width={50}
              style={{ margin: '0 5px' }}
            />
          ))}
        </div>
      )}
      {diceRoll && !isMoving && (
        <button onClick={handleDone}>Done</button>
      )}
    </div>
  );
};

export default DiceRoller;
