// src/components/Board.js
import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = {
  piece1, piece2, piece3, piece4, piece5, piece6, piece7, piece8
};

const Board = () => {
  const { players } = useContext(GameContext);
  const [zoom, setZoom] = useState(1);

  const zoomIn = () => setZoom(prev => Math.min(prev + 0.1, 2));
  const zoomOut = () => setZoom(prev => Math.max(prev - 0.1, 0.5));

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ marginBottom: '10px' }}>
        <button onClick={zoomOut}>➖ Zoom Out</button>
        <button onClick={zoomIn} style={{ marginLeft: '10px' }}>➕ Zoom In</button>
      </div>

      <div
        style={{
          position: 'relative',
          width: 600 * zoom,
          height: 600 * zoom,
          backgroundImage: `url(${boardImage})`,
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          border: '3px solid #333',
        }}
      >
        {players.map((p, i) => {
          const pieceImg = pieceMap[p.piece];
          if (!pieceImg) return null;

          return (
            <img
              key={i}
              src={pieceImg}
              alt={p.name}
              style={{
                position: 'absolute',
                top: 10 + i * 40,
                left: 10,
                width: 30 * zoom,
                height: 30 * zoom,
              }}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Board;
