import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');
const allPieces = [ 'piece1.png', 'piece2.png', /* â€¦ */ 'piece8.png' ];

const Lobby = () => {
  const {
    player, setPlayer,
    players, setPlayers,
    setGameState
  } = useContext(GameContext);
  const [name, setName] = useState('');
  const [hasJoined, setHasJoined] = useState(false);
  const [selectedPiece, setSelectedPiece] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', setPlayers);
    socket.on('readyUpdate', setReadyPlayers);
    socket.on('gameStart', () => setGameState('playing'));
    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      money: 1500,
      properties: [],
      piece: null,
      ready: false,
      tileId: 1,
      prevTile: null
    };
    setPlayer(newPlayer);
    socket.emit('joinLobby', newPlayer);
    setHasJoined(true);
  };

  const choosePiece = (piece) => {
    const updated = { ...player, piece };
    setSelectedPiece(piece);
    setPlayer(updated);
    socket.emit('selectPiece', { name: player.name || name, piece });
  };

  const handleReady = () => {
    socket.emit('playerReady', player.name || name);
  };

  const used = players.map(p => p.piece).filter(Boolean);
  const available = allPieces.filter(p => !used.includes(p));

  const allReady = players.length >= 2 &&
    players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{ /* centered full-screen styles */ }}>
      <h2>Monopoly Lobby</h2>

      {!hasJoined && (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <button onClick={joinLobby}>Join Lobby</button>
        </>
      )}

      {hasJoined && !selectedPiece && (
        <>
          <p>Select your piece:</p>
          <div style={{ display: 'flex', gap: 10 }}>
            {available.map((pc, i) => (
              <div key={i} onClick={() => choosePiece(pc)}>
                <img src={`/pieces/${pc}`} alt={pc} width={60} />
                <div>{pc.replace('.png','')}</div>
              </div>
            ))}
          </div>
        </>
      )}

      {selectedPiece && (
        <>
          <p>Welcome {player.name}! You chose <strong>{selectedPiece}</strong></p>
          <button onClick={handleReady}>Start Game</button>
        </>
      )}

      <hr />
      <h3>Players:</h3>
      <ul>
        {players.map((p,i) => (
          <li key={i}>
            {p.name} {p.piece && `ðŸ§©${p.piece}`} {readyPlayers.includes(p.name) && 'âœ…'}
          </li>
        ))}
      </ul>

      {allReady && <p style={{ color: 'green' }}>All readyâ€”starting!</p>}
    </div>
  );
};

export default Lobby;
