import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import './PlayerStats.css';

const PlayerStats = () => {
  const { players, player } = useContext(GameContext);

  return (
    <div style={{
      display: 'flex',
      gap: '1rem',
      padding: '0.5rem',
      background: '#f9f9f9'
    }}>
      {players
        .filter(p => p.name !== player?.name)
        .map((p,i) => (
          <div key={i} style={{
            flex: '1',
            border: '1px solid #ccc',
            borderRadius: 4,
            padding: '0.5rem',
            background: '#fff',
            textAlign: 'center'
          }}>
            <strong>{p.name}</strong>
            <p style={{ margin: '4px 0' }}>üí∞ {p.money}</p>
            <p style={{ margin: '4px 0' }}>
              üè† {p.properties.length ? p.properties.join(', ') : 'None'}
            </p>
          </div>
        ))
      }
    </div>
  );
};

export default PlayerStats;
