import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';
import { tiles } from '../data/tiles';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = { piece1, piece2, piece3, piece4, piece5, piece6, piece7, piece8 };

const Board = () => {
  const { players } = useContext(GameContext);
  const [size, setSize] = useState({ width: 600, height: 600 });
  const [coords, setCoords] = useState({ x: null, y: null });

  useEffect(() => {
    const img = new Image();
    img.onload = () => setSize({ width: img.width, height: img.height });
    img.src = boardImage;
  }, []);

  const handleClick = e => {
    const rect = e.currentTarget.getBoundingClientRect();
    setCoords({
      x: Math.floor(e.clientX - rect.left),
      y: Math.floor(e.clientY - rect.top)
    });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: 20 }}>
      <div
        onClick={handleClick}
        style={{
          position:'relative',
          width: size.width,
          height: size.height,
          background:`url(${boardImage})`,
          backgroundSize:'100% 100%',
          border:'3px solid #333',
          margin:'0 auto'
        }}
      >
        {players.map((p, i) => {
          if (!p.piece) return null;
          const tile = tiles.find(t => t.id === p.tileId) || { position: { x:40, y:520 }};
          const { x, y } = tile.position;
          return (
            <img
              key={p.name}
              src={pieceMap[p.piece]}
              alt={p.name}
              title={p.name}
              style={{
                position:'absolute',
                top: y + i*10,
                left: x + i*10,
                width:30,
                height:30,
                transform:'translate(-50%,-50%)',
                transition:'top .3s,left .3s'
              }}
            />
          );
        })}

        {coords.x !== null && (
          <div style={{
            position:'absolute', top:10, left:10,
            background:'rgba(255,255,255,0.9)',
            padding:'4px 8px', border:'1px solid #ccc', borderRadius:4
          }}>
            Tile: ({coords.x},{coords.y})
          </div>
        )}
      </div>
    </div>
  );
};

export default Board;
