import React from 'react';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';

export default function GameScreen() {
  return (
    <div style={{
      display:'flex', flexDirection:'column',
      minHeight:'100vh', background:'#f3f3f7', overflow:'hidden'
    }}>
      <div style={{
        flex:1, display:'flex',
        alignItems:'center', justifyContent:'center',
        position:'relative'
      }}>
        <div style={{
          minWidth:220, marginRight:48,
          background:'rgba(255,255,255,0.96)',
          borderRight:'2px solid #d3d3d3',
          borderRadius:12, padding:'16px 10px'
        }}>
          <h4 style={{ textAlign:'center', margin:0, marginBottom:10 }}>
            Other Players
          </h4>
          <PlayerStats />
        </div>
        <div style={{ position:'relative', zIndex:2 }}>
          <Board />
          <DiceRoller />
        </div>
      </div>
      <div style={{
        width:'100%', background:'#fff',
        borderTop:'2px solid #bbb',
        boxShadow:'0 -2px 10px #ececec',
        padding:'0 0 18px 0', zIndex:10
      }}>
        <Dashboard />
      </div>
    </div>
  );
}
