import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const Dashboard = () => {
  const { players, currentPlayerIndex } = useContext(GameContext);
  const current = players[currentPlayerIndex];
  if (!current) return null;

  return (
    <div style={{
      padding:'1rem', background:'#f0f0f0',
      borderTop:'1px solid #ccc', textAlign:'center'
    }}>
      <h3>{current.name}'s Dashboard</h3>
      <p><strong>Money:</strong> ${current.money}</p>
      <p><strong>Properties:</strong> {current.properties.length ? current.properties.join(', ') : 'None'}</p>
    </div>
  );
};

export default Dashboard;
