import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

const Dashboard = () => {
  const { players, currentPlayerIndex, isMoving, setCurrentPlayerIndex } = useContext(GameContext);
  const player = players[currentPlayerIndex];
  if (!player) return null;

  return (
    <div style={{ textAlign: 'center', padding: 16, borderTop: '1px solid #ccc', background: '#f0f0f0' }}>
      <h3>{player.name}'s Dashboard</h3>
      <p><strong>Money:</strong> ${player.money}</p>
      <p><strong>Properties:</strong> {player.properties.length ? player.properties.join(', ') : 'None'}</p>
      <button onClick={() => socket.emit('exitLobby')}>Exit Game</button>
      {!isMoving && <button onClick={() => setCurrentPlayerIndex((i) => (i + 1) % players.length)} style={{ marginLeft: 8 }}>Done</button>}
    </div>
  );
};

export default Dashboard;
