import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';
import { tiles } from '../data/tiles';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = {
  'piece1.png': piece1,
  'piece2.png': piece2,
  'piece3.png': piece3,
  'piece4.png': piece4,
  'piece5.png': piece5,
  'piece6.png': piece6,
  'piece7.png': piece7,
  'piece8.png': piece8,
};

const PIECE_SIZE = 30 * 3; // 3× original

const Board = () => {
  const { players } = useContext(GameContext);
  const [naturalSize, setNaturalSize] = useState({ width: 600, height: 600 });

  useEffect(() => {
    const img = new Image();
    img.onload = () => setNaturalSize({ width: img.width, height: img.height });
    img.src = boardImage;
  }, []);

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <div
        style={{
          position: 'relative',
          width: `${naturalSize.width}px`,
          height: `${naturalSize.height}px`,
          backgroundImage: `url(${boardImage})`,
          backgroundSize: '100% 100%',
          border: '3px solid #333',
          margin: '0 auto',
        }}
      >
        {players.map((p, i) => {
          const imgSrc = pieceMap[p.piece];
          if (!imgSrc) return null;
          const tile = tiles.find(t => t.id === p.tileId) || tiles.find(t => t.id === 1);
          const { x, y } = tile.position;
          return (
            <img
              key={p.name}
              src={imgSrc}
              alt={p.name}
              title={p.name}
              style={{
                position: 'absolute',
                top: y + i * 10 - PIECE_SIZE / 2,
                left: x + i * 10 - PIECE_SIZE / 2,
                width: PIECE_SIZE,
                height: PIECE_SIZE,
                transition: 'top 0.3s, left 0.3s',
              }}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Board;
