import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

const Dashboard = () => {
  const { players, currentPlayerIndex, isMoving } = useContext(GameContext);
  const player = players[currentPlayerIndex];

  if (!player) return null;

  return (
    <div style={{
      display:'flex',justifyContent:'center',
      marginTop:24,fontSize:'1.1rem'
    }}>
      <div style={{
        border:'2px solid #444',
        borderRadius:10,
        padding:'1.2rem 2rem',
        width:'80%',maxWidth:700,
        background:'#fff',boxShadow:'0 2px 6px rgba(0,0,0,0.1)',
        textAlign:'center'
      }}>
        <h3>{player.name}'s Dashboard</h3>
        <p><strong>Money:</strong> ${player.money}</p>
        <p><strong>Properties:</strong> {player.properties.length ? player.properties.join(', ') : 'None'}</p>
        <div style={{ marginTop:16 }}>
          <button onClick={() => socket.emit('exitLobby')}>Exit Game</button>
          {!isMoving && (
            <button
              onClick={() => {
                /* you can advance turn here if you like */
              }}
              style={{ marginLeft:16 }}
            >
              Done
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
