import React, { useState, useContext, useEffect } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyDisplay = () => {
  const [expandedIndexes, setExpandedIndexes] = useState({});
  const { gameState, currentPlayerId } = useContext(GameContext);

  // Get properties for a specific player
  const getPlayerProperties = (playerId) => {
    const player = gameState?.players?.[playerId];
    return tiles
      .filter(tile => tile.type === 'property')
      .filter(tile => player?.properties?.includes(tile.id));
  };

  // Get the IDs of players on the left side of the board
  const getLeftSidePlayers = () => {
    if (!gameState?.players) return [];
    return Object.keys(gameState.players).filter(playerId => {
      const position = gameState.players[playerId].position;
      return position === 'left-top' || position === 'left-bottom';
    });
  };

  const handlePropertyClick = (playerId, index) => {
    const playerProperties = getPlayerProperties(playerId);
    if (index === playerProperties.length - 1) return;

    setExpandedIndexes(prev => ({
      ...prev,
      [playerId]: prev[playerId] === index ? null : index
    }));
  };

  const renderPropertyStack = (position, playerId) => {
    const isLeftSide = position === 'left';
    const playerProperties = isLeftSide ? 
      getPlayerProperties(playerId) :
      getPlayerProperties(currentPlayerId);

    if (!playerProperties || playerProperties.length === 0) return null;

    const containerStyle = {
      position: 'absolute',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'visible',
      zIndex: 1, // Lower than the board's z-index
      ...(isLeftSide ? {
        // Left side stack - positioned relative to the outer board
        flexDirection: 'column',
        right: '-10px', // Overlap with the board
        height: '800px', // Match board height
        top: playerId === getLeftSidePlayers()[0] ? '0%' : '50%', // Split between left players
        bottom: playerId === getLeftSidePlayers()[0] ? '50%' : '0%',
        justifyContent: 'center'
      } : {
        // Top stack (existing style)
        top: '100%',
        left: '50%',
        transform: 'translateX(calc(-50% + 50px))',
        height: '280px',
        marginTop: '-3px',
        width: '800px',
        justifyContent: 'center'
      })
    };

    return (
      <div style={containerStyle}>
        {playerProperties.map((property, index) => {
          const isLastProperty = index === playerProperties.length - 1;
          const isExpanded = isLastProperty || expandedIndexes[playerId] === index;

          const basePosition = isLeftSide ? 
            index * 42 : // Vertical spacing for left side
            index * 56;  // Horizontal spacing for top

          let expandedOffset = 0;
          if (expandedIndexes[playerId] !== null && expandedIndexes[playerId] !== undefined) {
            if (index > expandedIndexes[playerId]) {
              expandedOffset = isLeftSide ? 154 : 154;
            }
          }

          const finalPosition = basePosition + expandedOffset;

          const cardStyle = {
            width: '210px',
            height: '280px',
            border: '2px solid #666',
            borderRadius: '12px',
            position: 'absolute',
            transition: 'all 0.3s ease',
            zIndex: isExpanded ? playerProperties.length : index,
            display: 'flex',
            flexDirection: 'column',
            padding: '14px',
            boxSizing: 'border-box',
            backgroundColor: 'rgb(173, 216, 230)',
            cursor: isLastProperty ? 'default' : 'pointer',
            ...(isLeftSide ? {
              top: finalPosition,
              transform: 'rotate(90deg)',
              transformOrigin: 'top left'
            } : {
              left: finalPosition
            })
          };

          const textContainerStyle = {
            transform: 'rotate(-90deg)',
            transformOrigin: 'left center',
            position: 'absolute',
            width: '280px',
            height: '210px',
            ...(isLeftSide ? {
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              padding: '14px'
            } : {
              top: '140px',
              left: '105px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              padding: '14px'
            })
          };

          return (
            <div
              key={property.id}
              onClick={() => !isLastProperty && handlePropertyClick(playerId, index)}
              style={cardStyle}
            >
              <div style={textContainerStyle}>
                <div style={{
                  fontSize: '1.96rem',
                  marginBottom: '14px',
                  textAlign: 'left',
                  whiteSpace: 'nowrap',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  maxWidth: '252px',
                  width: '100%',
                  color: '#333'
                }}>
                  {property.name}
                </div>
                <div style={{
                  fontSize: '1.4rem',
                  opacity: 1,
                  textAlign: 'center'
                }}>
                  <div>Cost: ${property.cost}</div>
                  <div>Rent: ${property.rent}</div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <>
      {/* Top stack - current player's properties */}
      {renderPropertyStack('top', currentPlayerId)}

      {/* Left side stacks - properties for players on the left */}
      {getLeftSidePlayers().map(playerId => 
        renderPropertyStack('left', playerId)
      )}
    </>
  );
};

export default PropertyDisplay;
