import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');
const piecesList = ['piece1','piece2','piece3','piece4','piece5','piece6','piece7','piece8'];

const Lobby = () => {
  const { player, setPlayer, players, setPlayers, setGameState } = useContext(GameContext);
  const [name, setName] = useState('');
  const [selectedPiece, setSelectedPiece] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', setPlayers);
    socket.on('readyUpdate', setReadyPlayers);
    socket.on('gameStart', () => setGameState('playing'));
    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      piece: '',
      ready: false,
      tileId: 1,
      prevTile: null,
      money: 1500,
      properties: []
    };
    setPlayer(newPlayer);
    socket.emit('joinLobby', newPlayer);
  };

  const choosePiece = piece => {
    setSelectedPiece(piece);
    setPlayer(p => ({ ...p, piece }));
    socket.emit('selectPiece', { name, piece });
  };

  const handleReady = () => {
    socket.emit('playerReady', name);
  };

  const used = players.map(p => p.piece).filter(Boolean);
  const available = piecesList.filter(p => !used.includes(p));
  const allReady = players.length >= 2 && players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{ textAlign:'center', padding:'2rem' }}>
      <h2>Monopoly Lobby</h2>

      {!player && (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <button onClick={joinLobby}>Join</button>
        </>
      )}

      {player && !selectedPiece && (
        <div>
          <p>Select your piece:</p>
          {available.map(p => (
            <img
              key={p}
              src={require(`../assets/pieces/${p}.png`)}
              alt={p}
              style={{ width:60, cursor:'pointer', margin:5 }}
              onClick={() => choosePiece(p)}
            />
          ))}
        </div>
      )}

      {selectedPiece && !readyPlayers.includes(name) && (
        <button onClick={handleReady} style={{ marginTop:20 }}>Ready ✅</button>
      )}

      <h3>Players in Lobby:</h3>
      <ul style={{ listStyle:'none', padding:0 }}>
        {players.map((p,i) => (
          <li key={i}>
            {p.name} {readyPlayers.includes(p.name) && '✅'}
          </li>
        ))}
      </ul>

      {allReady && <p>All set! Game starting…</p>}
    </div>
  );
};

export default Lobby;
