import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    player, players, currentPlayerIndex,
    originalCount, setOriginalCount,
    currentCount, setCurrentCount,
    isMoving, setIsMoving
  } = useContext(GameContext);

  const [diceRoll, setDiceRoll] = useState(null);

  // Only show when it's this player's turn and they're not already moving
  if (!player || players[currentPlayerIndex]?.name !== player.name || isMoving) {
    return null;
  }

  const rollDice = () => {
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    const total = d1 + d2;

    setDiceRoll([d1, d2]);
    setOriginalCount(total);
    setCurrentCount(total);
    setIsMoving(true);
  };

  const done = () => {
    // advance to next player
    setDiceRoll(null);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      <h3>Your Turn</h3>
      {!diceRoll && <button onClick={rollDice}>Roll Dice</button>}
      {diceRoll && (
        <>
          <div style={{ margin: '1rem' }}>
            <img src={`/dice/dice${diceRoll[0]}.png`} alt={`Die ${diceRoll[0]}`} width={50} />
            <img src={`/dice/dice${diceRoll[1]}.png`} alt={`Die ${diceRoll[1]}`} width={50} style={{ marginLeft: 10 }} />
          </div>
          {!isMoving && <button onClick={done}>Done</button>}
        </>
      )}
    </div>
  );
};

export default DiceRoller;
