// src/components/Lobby.js
import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');
const piecesList = [
  'piece1','piece2','piece3','piece4',
  'piece5','piece6','piece7','piece8'
];

const Lobby = () => {
  const {
    player,
    setPlayer,
    players,
    setPlayers,
    setGameState
  } = useContext(GameContext);

  const [name, setName] = useState('');
  const [selectedPiece, setSelectedPiece] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', pl => setPlayers(pl));
    socket.on('readyUpdate', ready => setReadyPlayers(ready));
    socket.on('gameStart', () => setGameState('playing'));

    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      piece: '',
      ready: false,
      tileId: 1,       // starting tile
      prevTile: null,  // needed for movement logic
      money: 1500,
      properties: []
    };
    setPlayer(newPlayer);
    socket.emit('joinLobby', newPlayer);
  };

  const choosePiece = piece => {
    setSelectedPiece(piece);
    setPlayer(p => ({ ...p, piece }));
    socket.emit('selectPiece', { name, piece });
  };

  const handleReady = () => {
    socket.emit('playerReady', name);
  };

  // figure out which pieces are already taken
  const used = players.map(p => p.piece).filter(Boolean);
  const available = piecesList.filter(p => !used.includes(p));
  const allReady =
    players.length >= 2 &&
    players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>Monopoly Lobby</h2>

      {/* Step 1: enter name */}
      {!player && (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <button onClick={joinLobby} style={{ marginLeft: 8 }}>
            Join
          </button>
        </>
      )}

      {/* Step 2: pick a piece */}
      {player && !selectedPiece && (
        <div style={{ marginTop: '1.5rem' }}>
          <p>Select your piece:</p>
          {available.map(p => (
            <img
              key={p}
              src={require(`../assets/pieces/${p}.png`)}
              alt={p}
              style={{
                width: 60,
                cursor: 'pointer',
                margin: 5,
                border: '2px solid #ccc',
                borderRadius: 4
              }}
              onClick={() => choosePiece(p)}
            />
          ))}
        </div>
      )}

      {/* Step 3: click Ready */}
      {selectedPiece && !readyPlayers.includes(name) && (
        <button
          onClick={handleReady}
          style={{ marginTop: 20, padding: '0.5rem 1rem' }}
        >
          Ready
        </button>
      )}

      {/* Show current lobby roster */}
      <h3 style={{ marginTop: '2rem' }}>Players:</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {players.map((p, i) => (
          <li key={i} style={{ margin: '0.5rem 0' }}>
            {p.name}{' '}
            {p.piece && (
              <>
                🧩<small>{p.piece}</small>
              </>
            )}{' '}
            {readyPlayers.includes(p.name) && <span>✅</span>}
          </li>
        ))}
      </ul>

      {/* All ready message */}
      {allReady && (
        <p style={{ marginTop: '1rem', color: 'green' }}>
          All set! 🚀 Starting…
        </p>
      )}
    </div>
  );
};

export default Lobby;
