import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const { player, currentPlayerId, socket } = useContext(GameContext);

  // LOGGING
  console.log("[DiceRoller.js] player:", player);
  console.log("[DiceRoller.js] currentPlayerId:", currentPlayerId);

  // Only show for player whose turn it is
  if (
    !player ||
    !player.socketId ||
    !currentPlayerId ||
    player.socketId !== currentPlayerId
  ) {
    console.log(
      `[DiceRoller.js] Not showing: player.socketId=${player?.socketId}, currentPlayerId=${currentPlayerId}`
    );
    return null;
  }

  const rollDice = () => {
    socket.emit('rollDice');
    console.log("[DiceRoller.js] Emitted rollDice");
  };

  return (
    <div style={{
      position: 'absolute',
      top: '12%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      <button onClick={rollDice}>Roll Dice</button>
    </div>
  );
};

export default DiceRoller;
