import React from 'react';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';

const GameScreen = () => (
  <div
    style={{
      display: 'flex',
      flexDirection: 'column',
      minHeight: '100vh',
      background: '#f3f3f7',
      overflow: 'hidden',
    }}
  >
    {/* Main row: PlayerStats left, Board center */}
    <div
      style={{
        flex: 1,
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
      }}
    >
      {/* PlayerStats on the left */}
      <div
        style={{
          minWidth: 220,
          height: '80%',
          marginRight: 48,
          background: 'rgba(255,255,255,0.96)',
          borderRight: '2px solid #d3d3d3',
          borderRadius: 12,
          boxShadow: '0 0 12px 0 #eee',
          padding: '16px 10px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'stretch',
        }}
      >
        <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
          Other Players
        </h4>
        <PlayerStats />
      </div>

      {/* Board in the center, DiceRoller overlays on top */}
      <div style={{ position: 'relative', zIndex: 2 }}>
        <Board />
        <DiceRoller />
      </div>
    </div>

    {/* Player's own dashboard at the bottom */}
    <div
      style={{
        width: '100%',
        background: '#fff',
        borderTop: '2px solid #bbb',
        boxShadow: '0 -2px 10px #ececec',
        padding: '0 0 18px 0',
        zIndex: 10,
      }}
    >
      <Dashboard />
    </div>
  </div>
);

export default GameScreen;
