import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = {
  piece1, piece2, piece3, piece4,
  piece5, piece6, piece7, piece8
};

const Board = () => {
  const { players } = useContext(GameContext);
  const [scale, setScale] = useState(1);

  const zoomIn = () => setScale(prev => Math.min(prev + 0.1, 2));
  const zoomOut = () => setScale(prev => Math.max(prev - 0.1, 0.5));

  const boardStyle = {
    transform: `scale(${scale})`,
    transformOrigin: 'center',
    transition: 'transform 0.3s ease',
    width: '600px',
    height: '600px',
    margin: '0 auto',
    position: 'relative',
    backgroundImage: `url('/board.png')`,
    backgroundSize: 'contain',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    border: '4px solid #444',
  };

  return (
    <div style={{ textAlign: 'center', paddingTop: '20px' }}>
      <div>
        <button onClick={zoomOut}>➖ Zoom Out</button>
        <button onClick={zoomIn}>➕ Zoom In</button>
      </div>

      <div style={boardStyle}>
        {players.map((p, i) => (
          p.piece && (
            <img
              key={i}
              src={pieceMap[p.piece]}
              alt={p.name}
              title={p.name}
              style={{
                position: 'absolute',
                top: 10 + i * 40,
                left: 10,
                width: '30px',
                height: '30px'
              }}
            />
          )
        ))}
      </div>
    </div>
  );
};

export default Board;
