import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';
import outerBoardImage from '../assets/outerBoard.png';
import { tiles } from '../data/tiles';

// Map piece names to images
const pieceImages = {};
for (let i = 1; i <= 8; i++) {
  pieceImages[`piece${i}.png`] = require(`../assets/pieces/piece${i}.png`);
}

// Desired piece height in px
const PIECE_HEIGHT = 165;

const Board = () => {
  const { players } = useContext(GameContext);
  const [boardSize, setBoardSize] = useState({ width: 600, height: 600 });

  useEffect(() => {
    const img = new window.Image();
    img.onload = () => setBoardSize({ width: img.width, height: img.height });
    img.src = boardImage;
  }, []);

  return (
    <div style={{ textAlign: 'center', marginTop: 20 }}>
      <div style={{ 
        position: 'relative', 
        width: boardSize.width, 
        height: boardSize.height, 
        margin: '0 auto' 
      }}>
        {/* Outer board */}
        <div
          style={{
            position: 'absolute',
            width: boardSize.width * 1.02,
            height: boardSize.height * 1.028,
            backgroundImage: `url(${outerBoardImage})`,
            backgroundSize: 'cover',
            top: -9,
            left: -15,
            zIndex: 1
          }}
        />
        
        {/* Main board */}
        <div
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            backgroundImage: `url(${boardImage})`,
            backgroundSize: '100% 100%',
            border: '3px solid #333',
            zIndex: 2
          }}
        >
          {players.map((p, i) => {
            if (!p) {
              console.log(`[Board.js] Missing player at index ${i}:`, p);
              return null;
            }
            const imgSrc = pieceImages[p.piece];
            if (!imgSrc) {
              console.log(`[Board.js] Player ${p.name} has invalid piece:`, p.piece);
              return null;
            }
            const tile = tiles.find(t => t.id === p.tileId);
            if (!tile) {
              console.log(`[Board.js] Player ${p.name} on missing tileId:`, p.tileId);
              return null;
            }
            const { x, y } = tile.position;
            return (
              <img
                key={p.socketId}
                src={imgSrc}
                alt={p.name}
                title={p.name}
                style={{
                  position: 'absolute',
                  top: y + i * 10,
                  left: x + i * 10,
                  height: PIECE_HEIGHT,
                  width: 'auto',
                  transform: 'translate(-50%, -50%)',
                  transition: 'top 0.3s, left 0.3s',
                  zIndex: 10,
                }}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Board;
