import React, { createContext, useState, useEffect } from 'react';
import socket from '../socket';

export const GameContext = createContext();

export function GameProvider({ children }) {
  const [player, setPlayer] = useState(null);
  const [players, setPlayers] = useState([]);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [gameState, setGameState] = useState('lobby');

  useEffect(() => {
    socket.on('lobbyUpdate', (newPlayers) => {
      setPlayers(newPlayers);
      console.log("[GameContext] Received lobbyUpdate:", newPlayers);
    });

    socket.on('gameStart', (payload) => {
      console.log('[GameContext] gameStart payload:', payload);
      setPlayers(payload.players);
      setSessionId(payload.sessionId);
      setGameState('playing');
      setCurrentPlayerId(payload.currentPlayerId); // Always set!
      console.log("[GameContext] gameStart - setCurrentPlayerId:", payload.currentPlayerId);
    });

    socket.on('turnEnded', ({ nextPlayerId }) => {
      setCurrentPlayerId(nextPlayerId);
      console.log("[GameContext] turnEnded - setCurrentPlayerId:", nextPlayerId);
    });

    socket.on('playerMoved', ({ playerId, tileId }) => {
      setPlayers((prev) =>
        prev.map((p) =>
          p.socketId === playerId ? { ...p, tileId } : p
        )
      );
    });

    return () => {
      socket.off('lobbyUpdate');
      socket.off('gameStart');
      socket.off('turnEnded');
      socket.off('playerMoved');
    };
  }, []);

  return (
    <GameContext.Provider
      value={{
        player,
        setPlayer,
        players,
        setPlayers,
        currentPlayerId,
        setCurrentPlayerId,
        sessionId,
        setSessionId,
        socket,
        gameState,
        setGameState,
      }}
    >
      {children}
    </GameContext.Provider>
  );
}
