// client/src/context/GameContext.js
import React, { createContext, useState, useEffect } from 'react';
import socket from '../socket';

export const GameContext = createContext();

export function GameProvider({ children }) {
  // the local “me”
  const [player, setPlayer] = useState(null);

  // all players in the lobby or game
  const [players, setPlayers] = useState([]);

  // whose turn it is next
  const [currentPlayerId, setCurrentPlayerId] = useState(null);

  // the session identifier once the game starts
  const [sessionId, setSessionId] = useState(null);

  // NEW: local game state (lobby or playing)
  const [gameState, setGameState] = useState('lobby');

  useEffect(() => {
    socket.on('lobbyUpdate', (newPlayers) => {
      setPlayers(newPlayers);
    });

    socket.on('gameStart', ({ players: ps, sessionId: sid }) => {
      setPlayers(ps);
      setSessionId(sid);
      setGameState('playing'); // <--- move to game screen
      // init turn to first player
      setCurrentPlayerId(ps[0]?.id || null);
    });

    socket.on('turnEnded', ({ nextPlayerId }) => {
      setCurrentPlayerId(nextPlayerId);
    });

    socket.on('diceResult', () => {
      // you can stash the last roll here if you like
    });

    socket.on('playerMoved', ({ playerId, tileId }) => {
      setPlayers((prev) =>
        prev.map((p) =>
          p.id === playerId ? { ...p, tileId } : p
        )
      );
    });

    return () => {
      socket.off('lobbyUpdate');
      socket.off('gameStart');
      socket.off('turnEnded');
      socket.off('diceResult');
      socket.off('playerMoved');
    };
  }, []);

  return (
    <GameContext.Provider
      value={{
        player,
        setPlayer,
        players,
        setPlayers,
        currentPlayerId,
        setCurrentPlayerId,
        sessionId,
        setSessionId,
        socket,
        gameState,
        setGameState,
      }}
    >
      {children}
    </GameContext.Provider>
  );
}
