import React, { useState, useContext, useEffect } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const { player, setPlayer, currentPlayerId, socket } = useContext(GameContext);
  const [keyInput, setKeyInput] = useState('');

  useEffect(() => {
    const handleKeyDown = (e) => {
      console.log(`Key pressed: ${e.key}`);  // Debug: Log each key press
      if (e.key === 'Enter') {
        console.log(`[DEBUG] Full input string before trimming: '${keyInput}'`);  // Debug: Log the string before trimming
        const commands = keyInput.trim().toLowerCase();  // Trim and convert to lowercase
        console.log(`[DEBUG] Full input string after trimming and lowercasing: '${commands}'`);  // Debug: Log the string after processing

        console.log(`[DEBUG] Available property IDs:`, tiles.map(tile => tile.id));  // Debug: Log available IDs

        // Process 'own#' commands
        const ownMatches = commands.matchAll(/own(\d+)/g);
        const matchesArray = Array.from(ownMatches);
        console.log(`[DEBUG] 'own#' matches found: ${matchesArray.length}`);  // Debug: Log number of 'own#' matches
        for (const match of matchesArray) {
          const propertyIdString = match[1];
          const propertyId = parseInt(propertyIdString, 10);  // Convert to number
          console.log(`[DEBUG] Processing 'own${propertyIdString}' (ID as number: ${propertyId})`);  // Debug: Log each 'own#' being processed
          const property = tiles.find(tile => tile.id === propertyId);  // Use the numeric ID for matching
          if (property) {
            setPlayer(prev => ({
              ...prev,
              properties: [...(prev.properties || []), propertyId]
            }));
            console.log(`[TEST] Player ${currentPlayerId} now owns property ${propertyId}`);
          } else {
            console.log(`[DEBUG] Property ID ${propertyId} not found in tiles`);  // Debug: Log if property is not found
          }
        }

        // Process 'testroll' or 'diceroll' command with detailed debugging
        console.log(`[DEBUG] Checking for 'testroll' or 'diceroll' in: '${commands}'`);  // Debug: Log the string being checked
        const testrollDetected = commands.includes('testroll');
        console.log(`[DEBUG] 'testroll' includes check result: ${testrollDetected} (index: ${commands.indexOf('testroll') !== -1 ? commands.indexOf('testroll') : 'not found'}, full string: '${commands}', substring test details: commands.includes('testroll') = ${commands.includes('testroll')}, commands.search(/testroll/) = ${commands.search(/testroll/)} )`);  // Debug: Log result and details for 'testroll'
        const dicerollDetected = commands.includes('diceroll');
        console.log(`[DEBUG] 'diceroll' includes check result: ${dicerollDetected} (index: ${commands.indexOf('diceroll') !== -1 ? commands.indexOf('diceroll') : 'not found'}, full string: '${commands}', substring test details: commands.includes('diceroll') = ${commands.includes('diceroll')}, commands.search(/diceroll/) = ${commands.search(/diceroll/)} )`);  // Debug: Log result and details for 'diceroll'
        if (testrollDetected || dicerollDetected) {
          console.log(`[DEBUG] Command detected: 'testroll' or 'diceroll' (matched in '${commands}')`);  // Debug: Log if either command is found
          const dice1 = Math.floor(Math.random() * 6) + 1;
          const dice2 = Math.floor(Math.random() * 6) + 1;
          console.log(`[TEST] Simulated dice roll: ${dice1} and ${dice2}`);
        } else {
          console.log(`[DEBUG] 'testroll' or 'diceroll' not detected in '${commands}'. Did you mean 'testroll' or 'diceroll'?`);  // Debug: Log if neither is found
        }

        setKeyInput('');  // Clear input after processing
        console.log(`[DEBUG] Input cleared`);  // Debug: Log that input has been cleared
      } else if (e.key.match(/^[0-9a-z]$/i)) {
        setKeyInput(prev => {
          const newInput = prev + e.key;
          console.log(`[DEBUG] Updated keyInput: '${newInput}'`);  // Debug: Log updated keyInput
          return newInput;
        });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [keyInput, currentPlayerId, setPlayer]);

  // Get only current player's properties
  const ownedProperties = tiles
    .filter(tile => tile.type === 'property')
    .filter(tile => player?.properties?.includes(tile.id));

  const handlePropertyClick = (index) => {
    // Don't allow clicking the last property as it's always expanded
    if (index === ownedProperties.length - 1) return;

    const clickedProperty = ownedProperties[index];
    console.log(`[clicked on property name: ${clickedProperty.name}]`);

    if (expandedIndex === index) {
      // Collapsing current property
      const movingProperties = ownedProperties
        .slice(index + 1, -1)
        .map(p => p.name)
        .join(', ');
      console.log(`moving ${movingProperties} back to initial positions`);
    } else {
      // Expanding new property
      const movingProperties = ownedProperties
        .slice(index + 1)
        .map(p => p.name)
        .join(', ');
      console.log(`moving ${movingProperties} to expanded positions`);
    }

    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '100%',
      left: '50%',
      transform: 'translateX(calc(-50% + 50px)) rotate(0deg)',
      height: '280px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'visible',
      marginTop: '-3px',
      width: '800px',
      justifyContent: 'center'
    }}>
      {ownedProperties.map((property, index) => {
        const isLeftSide = /* Logic to check if player is on left, e.g., from GameContext */ true;  // Replace with actual check
        const isLastProperty = index === ownedProperties.length - 1;
        const isExpanded = isLastProperty || expandedIndex === index;  // Always expand the last one

        let style = {
          width: '210px',
          height: '280px',
          border: '2px solid #666',
          borderRadius: '12px',
          position: 'absolute',
          left: isLeftSide ? '0' : index * 56,
          transition: 'all 0.3s ease',
          zIndex: isExpanded ? ownedProperties.length : index,
          display: 'flex',
          flexDirection: 'column',
          padding: '14px',
          boxSizing: 'border-box',
          backgroundColor: 'rgb(173, 216, 230)',
          cursor: isLastProperty ? 'default' : 'pointer',
          transform: isLeftSide ? 'rotate(90deg)' : 'rotate(0deg)',
          transformOrigin: isLeftSide ? 'top left' : 'center',
        };

        if (isLeftSide) {
          style.top = index * (window.innerHeight / ownedProperties.length);  // Divide space equally and expand downward
        }

        return (
          <div key={property.id} onClick={() => !isLastProperty && handlePropertyClick(index)} style={style}>
            <div style={{  // Keep text horizontal
              transform: isLeftSide ? 'rotate(-90deg)' : 'rotate(0deg)',
              transformOrigin: 'center',
              width: '280px',
              height: '210px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              color: '#333',
              fontSize: '1.68rem',
              fontWeight: 'bold',
              padding: '14px'
            }}>
              <div style={{ fontSize: '1.96rem', marginBottom: '14px', textAlign: 'left' }}>
                {property.name}
              </div>
              <div style={{ fontSize: '1.4rem', opacity: isExpanded ? 1 : 0 }}>
                <div>Cost: ${property.cost}</div>
                <div>Rent: ${property.rent}</div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PropertyDisplay;
