import { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const GameController = () => {
  const {
    players,
    setPlayers,
    currentPlayerIndex,
    originalCount,
    isMoving,
    setOriginalCount,
    setIsMoving
  } = useContext(GameContext);

  useEffect(() => {
    if (!isMoving || originalCount <= 0) return;

    const moveLoop = async () => {
      let steps = originalCount;
      const updated = [...players];
      const me = updated[currentPlayerIndex];

      while (steps > 0) {
        const current = tiles.find(t => t.id === me.tileId);
        if (!current || !Array.isArray(current.next) || current.next.length === 0) {
          break;
        }
        let opts = current.next.filter(o => o.from !== me.prevTile);
        if (opts.length === 0) opts = current.next;

        let nextOpt;
        if (opts.length === 1 || originalCount === 7) {
          nextOpt = opts[0];
        } else {
          const rollType = originalCount < 7 ? 'below' : 'above';
          nextOpt = opts.find(o => o.roll === rollType) || opts[0];
        }
        if (!nextOpt || typeof nextOpt.to !== 'number') break;

        me.prevTile = me.tileId;
        me.tileId = nextOpt.to;
        setPlayers([...updated]);

        steps--;
        // eslint-disable-next-line no-await-in-loop
        await new Promise(r => setTimeout(r, 400));
      }

      setOriginalCount(0);
      setIsMoving(false);
    };

    moveLoop();
  }, [
    isMoving,
    originalCount,
    players,
    currentPlayerIndex,
    setPlayers,
    setOriginalCount,
    setIsMoving
  ]);

  return null;
};

export default GameController;
