import React, { useContext } from 'react';
import Board from './Board';
import Dashboard from './Dashboard';
import DiceRoller from './DiceRoller';
import PlayerStats from './PlayerStats';
import { GameContext } from '../context/GameContext';

const GameScreen = () => {
  const { players, player, currentPlayerIndex } = useContext(GameContext);
  const currentPlayer = players[currentPlayerIndex];

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <div style={{ display: 'flex', flex: 1 }}>
        <div style={{
          width: '15%',
          background: '#f5f5f5',
          padding: '1rem',
          overflowY: 'auto'
        }}>
          <h4>Others</h4>
          <PlayerStats />
        </div>

        <div style={{
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative'
        }}>
          <Board />

          {/* Dice Roll Panel */}
          {player?.name === currentPlayer?.name && (
            <div style={{
              position: 'absolute',
              top: '10%',
              background: '#fff',
              border: '2px solid #333',
              borderRadius: '12px',
              padding: '1rem',
              boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
              zIndex: 1000
            }}>
              <h3>Your Turn</h3>
              <DiceRoller />
            </div>
          )}
        </div>

        <div style={{
          width: '15%',
          background: '#f5f5f5',
          padding: '1rem',
          visibility: 'hidden' // symmetry placeholder
        }} />
      </div>

      <div style={{
        padding: '1rem',
        borderTop: '1px solid #ccc',
        background: '#f0f0f0'
      }}>
        <Dashboard />
      </div>
    </div>
  );
};

export default GameScreen;
