import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';
import { tiles } from '../data/tiles';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = { piece1, piece2, piece3, piece4, piece5, piece6, piece7, piece8 };

const Board = () => {
  const { players } = useContext(GameContext);
  const [size, setSize] = useState({ width: 600, height: 600 });

  useEffect(() => {
    const img = new Image();
    img.onload = () => setSize({ width: img.width, height: img.height });
    img.src = boardImage;
  }, []);

  return (
    <div style={{ textAlign:'center', margin:'0 auto' }}>
      <div
        style={{
          position: 'relative',
          width: size.width,
          height: size.height,
          backgroundImage: `url(${boardImage})`,
          backgroundSize: '100% 100%',
          margin: '0 auto'
        }}
      >
        {players.map((p, i) => {
          const img = pieceMap[p.piece];
          if (!img) return null;
          const tile = tiles.find(t => t.id === p.tileId);
          const pos = tile ? tile.position : { x: 40, y: 520 };

          return (
            <img
              key={i}
              src={img}
              alt={p.name}
              title={p.name}
              style={{
                position: 'absolute',
                width: 30,
                height: 30,
                left: pos.x + i * 15 - 15,
                top: pos.y + i * 15 - 15,
                transition: 'all 0.3s'
              }}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Board;
