import React, { useContext, useState, useEffect } from 'react';
import bgImage from '../assets/bg.png';
import Dicebox from '../assets/diceBoard.png';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

export default function GameScreen() {
  const {
    player,
    players,
    setPlayer,
    setPlayers,
    currentPlayerId,
    socket,
    movementDone
  } = useContext(GameContext);

  const isMyTurn = player?.socketId === currentPlayerId;
  const [error, setError] = useState(null);

  // Determine metadata for the current tile
  const tileMeta = tiles.find(t => t.id === player?.tileId);

  // Debug logging to trace condition
  useEffect(() => {
    console.log('[GameScreen] Debug:', {
      isMyTurn,
      movementDone,
      tileMeta,
      "player.tileId": player?.tileId,
      "tileMeta.type": tileMeta?.type,
      "tileMeta.cost": tileMeta?.cost,
      "player.money": player?.money
    });
  }, [isMyTurn, movementDone, tileMeta, player]);

  // Listen for purchase events
  useEffect(() => {
    socket.on('purchaseSuccess', ({ socketId, money, properties }) => {
      console.log('[GameScreen] purchaseSuccess', { socketId, money, properties });
      const updated = players.map(p =>
        p.socketId === socketId ? { ...p, money, properties } : p
      );
      setPlayers(updated);
      if (player.socketId === socketId) {
        setPlayer({ ...player, money, properties });
      }
      setError(null);
    });

    // Add playerMoved event handler
    socket.on('playerMoved', ({ playerId, tileId }) => {
      console.log('[GameScreen] playerMoved', { playerId, tileId });
      if (playerId === player?.socketId) {
        setPlayer(prev => ({ ...prev, tileId }));
      }
      setPlayers(prev => 
        prev.map(p => p.socketId === playerId ? { ...p, tileId } : p)
      );
    });

    socket.on('purchaseFailed', ({ reason }) => {
      console.log('[GameScreen] purchaseFailed', reason);
      if (reason === 'insufficientFunds') {
        setError("You don't have enough money.");
      } else if (reason === 'alreadyOwned') {
        setError('You already own this property.');
      } else {
        setError('Cannot buy this property.');
      }
    });

    // Add rent payment handler
    socket.on('rentPaid', ({ payerSocketId, payerMoney, ownerSocketId, ownerMoney, amount, propertyName }) => {
      console.log('[GameScreen] rentPaid', { payerSocketId, ownerSocketId, amount });

      // Update players' money
      const updated = players.map(p => {
        if (p.socketId === payerSocketId) return { ...p, money: payerMoney };
        if (p.socketId === ownerSocketId) return { ...p, money: ownerMoney };
        return p;
      });
      setPlayers(updated);

      // Update current player's money if they were involved
      if (player.socketId === payerSocketId) {
        setPlayer({ ...player, money: payerMoney });
        setError(`You paid $${amount} rent to ${players.find(p => p.socketId === ownerSocketId)?.name} for landing on ${propertyName}`);
      } else if (player.socketId === ownerSocketId) {
        setPlayer({ ...player, money: ownerMoney });
        setError(`You received $${amount} rent from ${players.find(p => p.socketId === payerSocketId)?.name} for ${propertyName}`);
      }
    });

    return () => {
      socket.off('purchaseSuccess');
      socket.off('purchaseFailed');
      socket.off('rentPaid');
      socket.off('playerMoved');  // Clean up playerMoved listener
    };
  }, [socket, player, players, setPlayer, setPlayers]);

  const handleBuy = () => {
    console.log('[GameScreen] handleBuy invoked');
    setError(null);
    socket.emit('buyProperty');
  };

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        position: 'relative',
        backgroundImage: `url(${bgImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        overflow: 'hidden'
      }}
    >
      {/* Top bar with 4 quarters */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '240px',
          display: 'flex',
          zIndex: 100,
          background: 'rgba(80, 80, 80, 0.61)',
          boxShadow: '0 2px 8px rgba(0,0,0,0.2)'
        }}
      >
        {/* 1st quarter: Dice board */}
        <div
          style={{
            flex: 1,
            position: 'relative',
            height: '100%',
            borderRight: '1px solid #333',
            overflow: 'hidden'
          }}
        >
          {isMyTurn && (
            <img
              src={Dicebox}
              alt="Dice Board"
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'fill',
                pointerEvents: 'none'
              }}
            />
          )}
          <DiceRoller />
        </div>

        {/* 2nd quarter: Buy  */}
        <div
          style={{
            flex: 1,
            borderRight: '1px solid #333',
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          {(() => {
            const show = isMyTurn && movementDone && tileMeta?.type === 'property';
            console.log('[GameScreen] isMyTurn?', isMyTurn);
            console.log('[GameScreen] movementDone?', movementDone);
            console.log('[GameScreen] tileMeta)?', tileMeta);
            console.log('[GameScreen] render Buy?', show);
            return show ? (
              <>
                <button
                  onClick={handleBuy}
                  disabled={player.money < tileMeta.cost}
                  style={{ padding: '8px 16px', margin: '4px' }}
                >
                  Buy (${tileMeta.cost})
                </button>

                {error && (
                  <p style={{ color: 'tomato', marginTop: '8px' }}>{error}</p>
                )}
              </>
            ) : null;
          })()}
        </div>

        {/* 3rd & 4th quarters */}
        <div style={{ flex: 1, borderRight: '1px solid #333', height: '100%' }} />
        <div style={{ flex: 1, height: '100%' }} />
      </div>

      {/* Main content (board & dashboards) */}
      <div
        style={{
          marginTop: '240px',
          flex: 1,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative'
        }}
      >
        <div
          style={{
            minWidth: 220,
            height: '80%',
            marginRight: 48,
            background: 'rgba(206, 206, 206, 0.96)',
            borderRight: '2px solid #d3d3d3',
            borderRadius: 12,
            boxShadow: '0 0 12px 0 #eee',
            padding: '16px 10px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'stretch'
          }}
        >
          <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
            Other Players
          </h4>
          <PlayerStats />
        </div>

        <div style={{ position: 'relative', zIndex: 2 }}>
          <Board />
        </div>
      </div>

      {/* Bottom dashboard */}
      <div
        style={{
          width: '100%',
          background: '#fff',
          borderTop: '2px solid #bbb',
          boxShadow: '0 -2px 10px #ececec',
          padding: '0 0 18px 0',
          zIndex: 10
        }}
      >
        <Dashboard />
      </div>
    </div>
  );
}
