import React from 'react';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';

const GameScreen = () => (
  <div
    style={{
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      background: '#f3f3f7',
      overflow: 'hidden',
    }}
  >
    {/* Main game row: Board center, PlayerStats right */}
    <div
      style={{
        flex: 1,
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
        minHeight: 0,
      }}
    >
      {/* Board centered */}
      <div
        style={{
          flex: '0 0 auto',
          position: 'relative',
          zIndex: 1,
        }}
      >
        <Board />
        <DiceRoller />
      </div>

      {/* Spacer */}
      <div style={{ width: 40 }} />

      {/* Other Players' Stats on the right */}
      <div
        style={{
          flex: '0 0 220px',
          height: '80%',
          background: 'rgba(255,255,255,0.85)',
          borderLeft: '2px solid #d3d3d3',
          boxShadow: '0 0 12px 0 #eee',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-start',
          alignItems: 'stretch',
          padding: '18px 12px',
          marginRight: 30,
          borderRadius: 18,
        }}
      >
        <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
          Other Players
        </h4>
        <PlayerStats />
      </div>
    </div>

    {/* Player's own Dashboard pinned to the bottom */}
    <div
      style={{
        width: '100%',
        background: '#fff',
        borderTop: '2px solid #bbb',
        boxShadow: '0 -2px 10px #ececec',
        padding: '0 0 16px 0',
        zIndex: 10,
      }}
    >
      <Dashboard />
    </div>
  </div>
);

export default GameScreen;
