// src/components/PathPrompt.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const PathPrompt = () => {
  const { pendingPaths, choosePath } = useContext(GameContext);
  if (!Array.isArray(pendingPaths)) return null;

  return (
    <div style={{
      position: 'absolute',
      top: '50%', left: '50%',
      transform: 'translate(-50%,-50%)',
      background: 'rgba(0,0,0,0.8)',
      color: 'white',
      padding: 20,
      borderRadius: 8,
      zIndex: 2000,
    }}>
      <h3>Choose your path</h3>
      <div style={{ display: 'flex', gap: 10, flexWrap: 'wrap', justifyContent: 'center' }}>
        {pendingPaths.map(({ to, name }) => (
          <button
            key={to}
            onClick={() => choosePath(to)}
            style={{
              background: '#fff',
              color: '#000',
              padding: '8px 12px',
              borderRadius: 4,
              cursor: 'pointer'
            }}
          >
            {name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default PathPrompt;
