import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

export default function Dashboard() {
  const { player } = useContext(GameContext);
  if (!player) return null;

  return (
    <div style={{
      display:'flex', justifyContent:'center',
      marginTop:'1.5rem', fontSize:'1.1rem'
    }}>
      <div style={{
        border:'2px solid #444', borderRadius:10,
        padding:'1.2rem 2rem', width:'80%',
        maxWidth:700, backgroundColor:'#fff',
        boxShadow:'0 2px 6px rgba(0,0,0,0.1)',
        textAlign:'center'
      }}>
        <h3>{player.name}â€™s Dashboard</h3>
        <p><strong>Money:</strong> ${player.money}</p>
        <p><strong>Properties:</strong> {player.properties.join(', ')||'None'}</p>
      </div>
    </div>
  );
}
