import React, { useState, useContext, useEffect } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const { player, setPlayer, currentPlayerId, socket } = useContext(GameContext);
  const [keyInput, setKeyInput] = useState('');

  useEffect(() => {
    const handleKeyDown = (e) => {
      console.log(`Key pressed: ${e.key}`);  // Debug: Log each key press
      if (e.key === 'Enter') {
        console.log(`[DEBUG] Full input string: '${keyInput}'`);  // Debug: Log the entire input on Enter
        const commands = keyInput.toLowerCase();

        console.log(`[DEBUG] Available property IDs:`, tiles.map(tile => tile.id));  // Debug: Log available IDs

        // Process 'own#' commands
        const ownMatches = commands.matchAll(/own(\d+)/g);
        const matchesArray = Array.from(ownMatches);
        console.log(`[DEBUG] 'own#' matches found: ${matchesArray.length}`);  // Debug: Log number of 'own#' matches
        for (const match of matchesArray) {
          const propertyIdString = match[1];
          const propertyId = parseInt(propertyIdString, 10);  // Convert to number
          console.log(`[DEBUG] Processing 'own${propertyIdString}' (ID as number: ${propertyId})`);  // Debug: Log each 'own#' being processed
          const property = tiles.find(tile => tile.id === propertyId);  // Use the numeric ID for matching
          if (property) {
            setPlayer(prev => ({
              ...prev,
              properties: [...(prev.properties || []), propertyId]
            }));
            console.log(`[TEST] Player ${currentPlayerId} now owns property ${propertyId}`);
          } else {
            console.log(`[DEBUG] Property ID ${propertyId} not found in tiles`);  // Debug: Log if property is not found
          }
        }

        // Process 'testroll' or 'diceroll' command
        if (commands.includes('testroll') || commands.includes('diceroll')) {
          console.log(`[DEBUG] Command detected: 'testroll' or 'diceroll'`);  // Debug: Log if either command is found
          const dice1 = Math.floor(Math.random() * 6) + 1;
          const dice2 = Math.floor(Math.random() * 6) + 1;
          console.log(`[TEST] Simulated dice roll: ${dice1} and ${dice2}`);
        } else {
          console.log(`[DEBUG] 'testroll' or 'diceroll' not detected. Did you mean 'testroll' or 'diceroll'?`);  // Debug: Log if neither is found
        }

        setKeyInput('');  // Clear input after processing
        console.log(`[DEBUG] Input cleared`);  // Debug: Log that input has been cleared
      } else if (e.key.match(/^[0-9a-z]$/i)) {
        setKeyInput(prev => {
          const newInput = prev + e.key;
          console.log(`[DEBUG] Updated keyInput: '${newInput}'`);  // Debug: Log updated keyInput
          return newInput;
        });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [keyInput, currentPlayerId, setPlayer]);

  // Get only current player's properties
  const ownedProperties = tiles
    .filter(tile => tile.type === 'property')
    .filter(tile => player?.properties?.includes(tile.id));

  const handlePropertyClick = (index) => {
    // Don't allow clicking the last property as it's always expanded
    if (index === ownedProperties.length - 1) return;

    const clickedProperty = ownedProperties[index];
    console.log(`[clicked on property name: ${clickedProperty.name}]`);

    if (expandedIndex === index) {
      // Collapsing current property
      const movingProperties = ownedProperties
        .slice(index + 1, -1)
        .map(p => p.name)
        .join(', ');
      console.log(`moving ${movingProperties} back to initial positions`);
    } else {
      // Expanding new property
      const movingProperties = ownedProperties
        .slice(index + 1)
        .map(p => p.name)
        .join(', ');
      console.log(`moving ${movingProperties} to expanded positions`);
    }

    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '100%',
      left: '50%',
      transform: 'translateX(calc(-50% + 50px))',
      height: '280px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'visible',
      marginTop: '-3px',
      width: '800px',
      justifyContent: 'center'
    }}>
      {ownedProperties.map((property, index) => {
        const isLastProperty = index === ownedProperties.length - 1;
        const isExpanded = isLastProperty || expandedIndex === index;

        // Calculate base position (never changes)
        const basePosition = index * 56;

        // Calculate additional offset based on expanded state
        let expandedOffset = 0;

        if (expandedIndex !== null) {
          // If there's an expanded card
          if (index > expandedIndex) {
            // Cards after the expanded one move right by the full width of a card (210px) minus the base overlap (56px)
            expandedOffset = 154;
          }
        }

        // Calculate final position - all cards including last one follow the same rules
        const finalPosition = basePosition + expandedOffset;

        return (
          <div
            key={property.id}
            onClick={() => !isLastProperty && handlePropertyClick(index)}
            style={{
              width: '210px',
              height: '280px',
              border: '2px solid #666',
              borderRadius: '12px',
              position: 'absolute',
              left: finalPosition,
              transition: 'all 0.3s ease',
              zIndex: isExpanded ? ownedProperties.length : index,
              display: 'flex',
              flexDirection: 'column',
              padding: '14px',
              boxSizing: 'border-box',
              backgroundColor: 'rgb(173, 216, 230)',
              cursor: isLastProperty ? 'default' : 'pointer'
            }}
          >
            <div style={{
              transform: 'rotate(-90deg)',
              transformOrigin: 'left center',
              position: 'absolute',
              width: '280px',
              height: '210px',
              top: '140px',
              left: '105px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              color: '#333',
              fontSize: '1.68rem',
              fontWeight: 'bold',
              padding: '14px'
            }}>
              <div
                style={{
                  fontSize: '1.96rem',
                  marginBottom: '14px',
                  textAlign: 'left',
                  whiteSpace: 'nowrap',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  maxWidth: '252px',
                  width: '100%'
                }}>
                {property.name}
              </div>
              <div style={{
                fontSize: '1.4rem',
                opacity: 1,
                textAlign: 'center'
              }}>
                <div>Cost: ${property.cost}</div>
                <div>Rent: ${property.rent}</div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PropertyDisplay;
