import React, { useState } from 'react';
import { tiles } from '../data/tiles';

const PropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);

  // Filter only property type tiles
  const properties = tiles.filter(tile => tile.type === 'property');

  const handlePropertyClick = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '100%', // Position right under the board
      left: '50%',
      transform: 'translateX(-50%)',
      height: '200px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'hidden',
      marginTop: '-3px' // Overlap with board border
    }}>
      {properties.map((property, index) => {
        const isExpanded = expandedIndex === index;
        const isRightOfExpanded = expandedIndex !== null && index > expandedIndex;
        const isLeftOfExpanded = expandedIndex !== null && index < expandedIndex;
        
        return (
          <div
            key={property.id}
            onClick={() => handlePropertyClick(index)}
            style={{
              width: '150px',
              height: '200px',
              backgroundColor: 'rgba(80, 80, 80, 0.9)',
              border: '2px solid #666',
              borderRadius: '12px',
              position: 'relative',
              cursor: 'pointer',
              transition: 'transform 0.3s ease',
              transform: `translateX(${isRightOfExpanded ? 150 : isLeftOfExpanded ? -40 : 0}px)`,
              marginLeft: index === 0 ? 0 : '-110px', // Overlap properties
              zIndex: isExpanded ? properties.length : properties.length - index,
              display: 'flex',
              flexDirection: 'column',
              padding: '10px',
              boxSizing: 'border-box'
            }}
          >
            {/* Property Name - Rotated */}
            <div style={{
              transform: 'rotate(-90deg) translateX(-50%)',
              transformOrigin: 'left top',
              width: '180px', // Height of container
              height: '40px',
              display: 'flex',
              alignItems: 'center',
              color: 'white',
              fontSize: '1.2rem',
              fontWeight: 'bold',
              position: 'absolute',
              top: '180px',
              left: '20px',
              whiteSpace: 'nowrap',
              overflow: 'hidden',
              textOverflow: 'ellipsis'
            }}>
              {property.name}
            </div>

            {/* Property Details - Only visible when expanded */}
            <div style={{
              opacity: isExpanded ? 1 : 0,
              transition: 'opacity 0.3s ease',
              color: 'white',
              marginTop: 'auto',
              marginBottom: '10px',
              textAlign: 'center'
            }}>
              <div style={{ marginBottom: '5px' }}>Cost: ${property.cost}</div>
              <div>Rent: ${property.rent}</div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PropertyDisplay;
