import React from "react";
import Tile from "./Tile";
import "../styles/game.css";

function Board({ players, properties }) {
  const tiles = Array(16).fill(null).map((_, i) => {
    if (i === 0) return { type: "start", name: "START" };
    if (i > 0 && i <= 11) return { ...properties[i - 1], type: "property" };
    return { type: "blank", name: "Blank" };
  });

  return (
    <div className="board">
      {tiles.map((tile, idx) => (
        <Tile key={idx} index={idx} tile={tile} players={players.filter(p => p.pos === idx)} />
      ))}
    </div>
  );
}

export default Board;
